css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {

body:not(.index2) {
margin: 0 auto !important;
padding-top: 0 !important;
width: 934px !important;
}

body:not(.index2) > header.container {
position: relative !important;
width: 934px !important;
}

body:not(.index2) > header.container hgroup {
margin-right: 0 !important;
}

body:not(.index2) #slashboxes,
body:not(.index2) #slashboxes-mq {
display: none !important;
}

/* ==== Comments ==== */

li.comment {
margin-bottom: 0.5em !important;
}

li .commentBody {
padding-top: 1em !important;
}

li.oneline .commentBody {
padding-top: 0 !important;
}

#comments ul ul, #comments ul li ul li {
margin: 0.75em 0.375em 0.75em !important;
}

#comments {
margin-left: 1.5em !important;
}

.commentBody p:last-child, .commentBody > div {
margin-bottom: 0.5em !important;
}

#comments .oneline {
height: 28px !important;
text-indent: 10px !important;
}

#comments .oneline h4, #comments .oneline .commentBody,
#comments .oneline .commentBody {
line-height: 28px !important;
}

#firehose article header .details, #comments article header .details, #comments .full .detail, #modal_box #editor #editpreview .details, #comments .details {
font-size: 1em !important;
}

#comments h4, #comments .details {
padding: 4px 15px !important;
}

#comments  .commentBody {
padding-left: 15px !important;
padding-right: 15px !important;
}

#comments .oneline h4 {
padding: 0 5px !important;
}

#commentlisting.d1 .commentSub, #wide #commentlisting .commentSub, #comments .commentSub {
padding: 0 15px !important;
}

.oneline .current {
margin-top: -0.55em !important;
}

/* ==== Sidebars ==== */

body:not(.index2) #content #firehose {
margin: 0 !important;
}

body:not(.index2) div.col_1 {
display: none !important;
}
}
