css:
@-moz-document domain('gizmodo.com'), domain('deadspin.com'), domain ('jezebel.com'), domain('kotaku.com'), domain('io9.org'), domain('jalopnik.com'), domain('gawker.com'), domain('lifehacker.com')
{	
	#sidebar,
	#header,
	.permalink_ads
	{ display: none !important }

.sponsored,
.sponsored + .content.withpic,
.sponsored + .content.withpic + .postmeta.smalltype
{ display: none !important }


#menubar-bg
{margin-top: 12px !important}

#container
{ background-image: none !important}

#wrapper
{ margin: 0 !important}

.content, .comment
{ margin-left: 0px !important; width: 680px !important;}

.noimage_title
{
width: auto !important;
}

.postBigPic, .postBigVid{
background-color: inherit !important;
border: none !important;
width: 680px !important;
}

.bigExcerptLeft
{ width: 330px !important; }

.content.permalink IMG[width="500"],
.content.permalink [id^="obj_"] OBJECT[width="500"][codebase*="flash"],
.content.permalink OBJECT[width="500"],                
.content.permalink [id="flashObj"][width="500"][codebase*="flash"]
{padding-left: 90px !important; padding-right: 90px !important; border:0 !important;}

.content.permalink [id="flashObj"][width="486"][codebase*="flash"],
.content.permalink DIV OBJECT EMBED[type*="flash"],
.content.permalink IFRAME
{ margin-left:auto !important; margin-right:auto !important; display: block !important}

.ctext
{ width: 508px !important}

#comments .commentbox
{ width: 510px !important}

#comments .cb_textarea
{ width: 437px !important}
}
