css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {


body { font-family: "Droid Sans","Segoe UI",sans-serif !important; font-size: 12px!important; color: #eee !important;  margin: 0 auto !important; padding: 0; background: #222 !important; z-index:-100}
form[name="delform"]{ width:85% !important; max-width: 1280px !important; margin: 0 auto !important; background: #101010; padding: 25px 5px 5px 5px; border-left: 1px solid #222; border-right:1px solid #222; float:left }
form[action=""]{padding:0px !important;}

#recent-images li{ background: #1c1c1c !important; }
#header { width: 100% !important; }
#navtop { font-size: 12px !important; border-bottom: 1px solid #1c1c1c; top: 0; position: fixed;
 padding: 0; width: 100% !important; padding: 5px !important; background: #222 !important;
 color: #8899AA !important; text-align: center !important; z-index: 1009 }

div.logo { position: fixed; width:200px !important; text-align:right;  z-index:-99; right:5px;}
div.logo font span { visibility: visible; font-size: 20px !important; color: white; }
div.logo img{display:none;}

SPAN[style="left: 5px; position: absolute;"] {display:none !important;}
center font[color="red"]{display:none !important;}
BODY TABLE[width="100%"] { padding: 0 !important; padding-left: 10px !important; padding-right: 10px !important; margin-bottom: -1px;}
BODY TABLE[width="100%"] TH { padding: 8px !important;  }

.postarea { padding-top:20px !important; width:85%; max-width: 1280px !important; }
.postarea form div {border: none !important;}
.postarea input{ }
.postarea form { border-bottom: 1px solid #212121; padding-bottom: 18px !important;}
.postarea table { margin-top: -10px; margin-bottom: -10px; }
.postblock { border: 0 !important; background: #1c1c1c !important; color: #aaa !important; }
a { color: #999 !important; text-decoration: none !important; border: 0 !important; }
a:hover { color: #bb0000 !important; background: #222 !important; }

th { background: #222 !important; padding: 10px !important; display:none;}
td.reply { width: 900px !important; border: 0 !important; background-color:#1c1c1c !important; color: #FFF !important; clear: both; }
td.replyhl { width: 900px !important; border: 0 !important; background-color:#444444 !important; color: #FFF !important; }
.pages { background: #222 !important; border: 0 !important; text-align: center !important; float:right !important }
.pages td, .pages td b {font-size: 12px !important; color: #666 !important;}

.postername, .abbr { color: #599 !important; }
.commentpostername { color: #CCC !important; }
.postertrip { color: #688 !important; }
.filetitle { color: #6666ff !important; }
a img { clear: both !important; margin: 0 10px 10px 10px !important; border: 1px solid #AAA !important; position:relative; z-index:100; }
.deletebuttons input:not([type="checkbox"]) { width: 130px !important; display: inline !important; }
.deletebuttons input[type="checkbox"] { position: relative; top: 3px; }
.quotejs { visibility: visible;   }

.replytitle{ color: #6666ff !important; }

/*body form input {display: none !important; }*/

body form { font-size: 12px !important; }
td {font-size: 12px !important;} 

.omittedposts {color: #777 !important; visibility: visible !important; margin-left: 10px;}
.filesize {  padding-left: 2px; display: inline-block; padding-top: 3px; padding-bottom: 6px; }

/* top right bottom left */
body form blockquote { background: #333333; padding: 10px; margin: 8px 2px 8px 20px;}
td blockquote { background: none !important; margin: 0 !important; padding: 5px !important;  }
form td{padding: 2px 6px !important}



 #navtopr, #hd, #ft, hr, td small, .doubledash, #footer, .rules, center font h3, center font small, center small font { display: none !important; }


/*4chan ext***************************/
img[class*="4chan_ext"]{
display: inline;
margin: 0 !important; 
border: 0 !important;
}

div[id*="twi_table"]{


}

.reply{ 
background: #1C1C1C !important;

border: 1px solid #444 !important;
color: #EEE !important;
font-size: 12px !important;
}

.postername , .postertrip, .filetitle, .commentpostername{
font-size: 12px!important;
}

.reply[id*="ext"]{ 
padding: 1px !important;
}

div[id*="twi_table"] .postblock{
font-size: 12px !important;
padding: 3px !important;
}

form[id*="4chan_qr_form"]{
width: auto !important;
padding: 5px;

}

span[class*="4chan_win_header_buttons"] img{
display: inline;
padding: 1px;
}
/**************************************/

/*Center Images When you look at them*/
img:not(url^="chrome-extension://pinelipedelckihohgdlpcclgocodhjj/"){ display: block; margin: 0 auto; position:absolute; z-index:-99; }

/*Remove Advertising*/
img[src*="http://static.4chan.org/support"]{
display:none !important;
}
/*4chan plus stuff*/
#popup {
position: absolute; border: 1px solid #777 !important; border-radius: 0px !important; -webkit-box-shadow: none !important; z-index: 995; display: none; padding: 0px; background-color: inherit;
}
#quickrep {
position: fixed; border: 0px solid white !important; border-radius: 0px !important; -webkit-box-shadow:none !important; right:0 !important; bottom:0 !important; top: auto !important; left: auto !important; z-index: 992; display: none; background: #333 !important;
}
#navbar {
position: fixed; bottom: 0px; height: 20px; padding: 5px 5px 3px; border-top: 1px solid; border-left: 1px solid; border-right: 1px solid; border-bottom:none; border-radius:0px ; -webkit-box-shadow: none !important; font-size: 15px; z-index: 997;
}
#navbar:not(:hover){opacity: .2 !important;}
.navbar2{left:40% !important;}
form[name="delform"]{
padding:15px 0 20px 0 !important;
}
/*filter.next stuff*/
.sage:not(:hover) blockquote,
.sage:not(:hover) img {
  display: none !important;
}
#filter{z-index:1000;}

/*Rollin' in the .3 update.*/
img#recaptcha_reload { height: 0px !important; padding-top: 18px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

img#recaptcha_switch_audio { height: 0px !important; padding-top: 15px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

img#recaptcha_whatsthis { height: 0px !important; padding-top: 16px !important; width: 25px !important; background-image: url(data:image/png;base64,) !important; margin: 0 !important; border: none !important; }

#recaptcha_logo, #recaptcha_tagline { display: none; }
}

/*** FRONTPAGE ***/

@-moz-document url(http://www.4chan.org/){



HTML > BODY {padding: 10px 0px !important; color: #dddddd !important; }



#hd, #close-announce

  { display: none !important;

background: #222222 !important; color: #dddddd;

}



.boxbar, #preview-tooltip-nws H2, #preview-tooltip-ws H2

  {  background: #222222 !important; color: #dddddd;

    text-align: center !important;

    color: #bb0000 !important;

    border-bottom: 1px solid rgba(0,0,0,.2) !important; }

#announce .boxcontent

  { text-align: center !important; background: #222222 !important; color: #dddddd; }

.box-outer, #preview-tooltip-nws, #preview-tooltip-ws

  {  background: #222222 !important; color: #dddddd;

    border-width: 12px !important;

}

.menubutton { background-image: none !important; background: #222222 !important; color: #dddddd; }

#filter-button, #option-button

  { letter-spacing: 0px !important;

    padding : 0px !important;

    margin: 0 !important; 

 }

#filter-button:hover, #option-button:hover

  { opacity: .7 !important;

 }

.yui-menu-shadow

  { display: none !important; }



#recent-images LI

  { background: none !important;  background: #222222 !important; color: #dddddd; }

#boards .column

  { 

width: 12em !important;

    text-transform: lowercase !important;

 background: #222222 !important; color: #dddddd;

 }

.column > H3, .p_postername

  { color: #bb0000 !important; }



.box-outer.right-box, .box-outer.left-box,.box-outer.top-box

  {  background: #222222 !important; color: #dddddd; }



.box-outer, #preview-tooltip-nws, #preview-tooltip-ws

  {  background: #222222 !important; color: #dddddd;

    border: 5px solid #101010 !important;

}



#preview-tooltip-nws, #preview-tooltip-ws{

 background: #222222 !important; color: #dddddd;

    padding: 5px !important;

}



.boxcontent img{

 background: #222222 !important; color: #dddddd;

    margin: 0 auto !important; 

}



}



@-moz-document url-prefix(http://www.4chan.org/framesnav){



BODY

  { 

font: 11px verdana !important;

 }

#logo

  { background: none !important; 

height: 0 !important;

padding-bottom: 5px !important;

}

.plus

  { background: #DDD !important;

    border: none !important; }

H2

  { background: #1C1C1C !important;

    color: #FFF !important; }

LI:hover

  { text-decoration: none !important;

}

li{

color: #6666ff !important;

padding-left: 5px !important;

padding-right: 5px !important;

}



}
@-moz-document domain("images.4chan.org") {
img{ display: block; margin: 0 auto;}
}
