css:
@-moz-document domain("forums.denverbroncos.com") {




html, body {
background-color:#0E284B;
color:#FFFFFF !important;
}

#header_leader, #header_leader2 {
background-image:url !important;
display: none !important;
}

#page {
background-color:#0E284B !important;
display:inline;
float:left;
width:960px;
}

#content {
background-image:url !important;
background-repeat:no-repeat;
display:inline;
float:left;
font-style:normal;
margin:0px;
width:918px;
}

#leftContent, #rightContent {
background-image:url !important;
background-position:left top !important;
background-repeat:no-repeat !important;
display:inline !important;
float:left !important;
height:585px !important;
width:21px !important;
}

td.rowColor1, td.rowColor1Active {
background:#0E284B !important;
border: 1px solid #4D4D4D;
}
td.rowColor2, td.rowColor2Active {
background:#0C2343 !important;
border: 1px solid #4D4D4D;
}

.alt1 {
background:#0E284B !important;
border: 1px solid #4D4D4D !important
}
.alt2 {
background:#0C2343 !important;
border: 1px solid #4D4D4D !important
}

.alt2, .alt2Active {
color: #F5F5F5 !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
.alt2 a:link, .alt2_alink, .alt2Active a:link, .alt2Active_alink {
color: #FF6600 !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
.alt2 a:visited, .alt2_avisited, .alt2Active a:visited, .alt2Active_avisited {
color: #FF6600 !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
.alt2 a:hover, .alt2 a:active, .alt2_ahover, .alt2Active a:hover, .alt2Active a:active, .alt2Active_ahover {
color: #FF6600 !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}

.vbmenu_control {
background:#000000 url no-repeat scroll left bottom !important;
color: #F5F5F5 !important;
font: bold 8pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
border: 1px solid #4D4D4D;
padding-left: 3px !important;
padding-right: 3px !important;
white-space: nowrap !important;
}
td.alt2 span.smallfont {
color:#F5F5F5!important;
}

.vbmenu_control a:link, .vbmenu_control_alink {
color: #FF6600  !important;
background:#000000 url no-repeat scroll left bottom !important;
height: 30px !important;
padding: 0 !important;
padding-left: 5px !important;
padding-bottom: 0px !important;
padding-top: 4px !important
}

.vbmenu_popup {
background: #F05E22 !important;
color: #FFFFFF!important;
font: bold 12px tahoma,verdana,helvetica,sans-serif !important;
border: 2px solid #F4814A !important;
}

.vbmenu_option a:link, .vbmenu_option_alink, .vbmenu_option a:visited, .vbmenu_option_avisited, .vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover {
color: #0E284B !important;
font: bold 11px Arial, Helvetica, sans-serif !important;
white-space: nowrap !important;
cursor: pointer !important;
}

.vbmenu_option {
background: #FFFFFF !important;
color: #FF6600 !important;
font: Bold 11px Arial, Helvetica, sans-serif !important;
white-space: nowrap !important;
cursor: pointer !important;
}

.vbmenu_hilite {
background: #0E284B !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink, .vbmenu_hilite a:visited, .vbmenu_hilite_avisited, .vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover {
color: #F5F5F5 !important;
font: Bold 11px Arial, Helvetica, sans-serif !important;
}

table.tborder .thead {
height:36px !important;
line-height:13px !important;
padding-top: 10px !important;
text-align:left !important;
text-transform:uppercase !important;
}
.thead a{
color: #F5F5F5 !important;
}

.tcat {
background:#000000 url no-repeat scroll left bottom !important;
color: #FF6600 !important;
font: bold 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
height: 22px !important;
padding: 0 !important;
border: 1px solid #4D4D4D !important;
padding-left: 5px !important;
padding-bottom: 4px !important;
padding-top: 4px !important
}
.tcat a {
font: 16px "Arial narrow", tamaho, Helvetica, sans-serif !important;
font-style:normal !important;
font-weight:bold !important;
}

table.tborder tbody tr td.panelsurround {
background:#0C2343 url no-repeat scroll left bottom !important;
border: 1px solid #4D4D4D !important;
padding-left: 5px !important;
padding-right: 25px !important;
padding-bottom: 4px !important;
padding-top: 4px !important
}

li#minicontact, li#userlists {
height:36px !important;
line-height:14px !important;
padding-top: 2px !important;
text-align:left !important;
font: Bold 14px Arial!important;
}
h1 {
color: #FF6600!important;
font: bold 14pt tahoma  !important;
}
h2 {
color: #FFFFFF!important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:11px !important;
}
li.thead.tborder {
background:#000000 url no-repeat scroll left bottom !important;
color: #777777 !important;
font: bold 9pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
height: 18px !important;
padding: 0 !important;
border: 1px solid #4D4D4D !important;
padding-left: 5px !important;
padding-bottom: 4px !important;
padding-top: 4px !important;
text-transform:uppercase !important;
}

.alt2 table tbody tr td .bigusername {
color: #FF6600!important;
font: bold 14pt tahoma !important;
}

p strong {
color: #FF6600!important;
font: 16px Arial , Helvetica, sans-serif !important;
font-style:normal !important;
font-weight:bold !important;
}


.smallfont a {
color: #FF6600!important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:13px !important;
}

legend {
color: #FF6600!important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
.fieldset, .fieldset td, .fieldset p, .fieldset li {
color: #FFFFFF!important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
div#vB_Editor_001_controls table, div#vB_Editor_QR_controls {
background:#e1e1e2 url repeat-x scroll left top !important;
border: 1px solid #364f78!important;
color:#000000!important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}
.panel {
background:#0E284B url repeat-x scroll left top !important;
padding:10px;
width:100%;
}
.time{
color: #81999F !important;
}

a div {
color:#81999F !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}

td a {
color: #FF6600 !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-size:12px !important;
}

.smallfont {
color:#F5F5F5!important;
}

td.alt1 .smallfont {
color:#FF6600 !important;
}

div.smallfont em{
color:#FFFFFF !important;
}
td.thead {
color: #FFFFFF !important;
font: Bold 13px tahoma,verdana,helvetica,sans-serif !important
}

td.alt1, td.alt2, .alt1Active, .alt2Active {
background:#0E284B url repeat-x scroll left bottom !important;
border: 1px solid #4D4D4D
}


td.alt1 p {
color:#FFFFFF !important;
}

.normal {
color:#EFEFEF !important;
font-family:tahoma,verdana,helvetica,sans-serif !important;
font-weight:bold !important;
font-size:13px !important;
height: 30px !important;
padding: 0 !important;
padding-left: 5px !important;
padding-bottom: 4px !important;
}




}
