@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {


/** Reduce width of the Page **/
body {
  position: relative !important;
  width: 70% !important;
  min-width: 700px !important;
  margin: 0 auto !important;
  padding: 0 !important;
  background: #FFF;
}

body > * { padding: 0 1% !important }


/* Generic Stuff
  --------------- */

/** Font Settings **/
* {
  font-family: Ubuntu,"Lucida Grande",Calibri !important;
  font-size: 10pt !important;
}

h1 { font-size: 30pt !important; }
h2 { font-size: 20pt !important; }
h3 { font-size: 16pt !important; }
h4 { font-size: 14pt !important; }

li { margin: 2px 0; }

/** Rating Icons **/
#discussions li, #front-page-best li, #front-page-newest li { 
  list-style: none inside none !important;
  padding: 0 0 0 20px !important;
}
#discussions .good-rating, #front-page-best .good-rating, #front-page-newest .good-rating {
  background: url(data:image/png;base64,) center left no-repeat;
}
#discussions .bad-rating, #front-page-best .bad-rating, #front-page-newest .bad-rating {
  background: url(data:image/png;base64,) center left no-repeat;
}
#discussions .ok-rating, #front-page-best .ok-rating, #front-page-newest .ok-rating {
  background: url(data:image/png;base64,) center left no-repeat;
}
#discussions .no-rating, #front-page-best .no-rating, #front-page-newest .no-rating {
  background: url(data:image/png;base64,) center left no-repeat;
}
#discussions li {
  background-position: left 1px !important;
}

/* Code */
code, code *,
pre, pre * {
  font-family: Consolas !important;
  font-size: 8pt !important;
}


/* Navigation 
  ------------ */
#breadcrumbs, #other-links {
  position: absolute;
  margin: 0 !important;
  padding: 10px 10px 6px !important;
  height: 22px;
  width: 50% !important;
  background: #38abe3 !important;
  color: #FFF;
  border-bottom: 4px solid #FFF !important;
}
#other-links { text-align: right; }
#breadcrumbs a, #other-links a {
  color: #FFF !important;
  font-weight: bold;
  text-decoration: none !important;
}
#search {
  display: inline-block !important;
  margin-top: -4px;
}
#search-terms {
  -moz-appearance: none;
  -moz-border-radius: 2px 0 0 2px !important;
  border: none;
  padding: 4px !important;
  margin-right: -3px;
}
#search-submit{
  -moz-appearance: none;
  -moz-border-radius: 0 2px 2px 0 !important;
  border: none;
  padding: 3px !important;
  background: #cfecff;
  color: #1e79a7;
  font-weight: bold;
}

/** Page Title **/
body > h1 {
  margin: 0 !important;
  padding: 45px 0 4px 10px !important;
  background: #38abe3 !important;
  color: #FFF !important;
}


/* Content Modification
  ---------------------- */

/** Homepage **/
#front-page-best { float: left; } #front-page-newest { float: right }
#front-page-best, #front-page-newest {
  width: 47%;
  max-width: none !important;
  margin: 1%;
  padding: 0 !important;
  background: #cfecff;
  border: 1px solid #abdafb;
}
#front-page-best h2, #front-page-newest h2 {
  margin: 5px 10px;
}
#front-page-best ul, #front-page-newest ul {
  list-style-position: inside;
  margin: 10px;
  padding: 0 5px;
}
.additional-link {
  margin: 5px;
  list-style: none;
}


/** Browsing Styles **/
#search-options input,
#search-options select {
  -moz-appearance: none;
  -moz-border-radius: 2px;
  border: 1px solid #1e79a7;
}
#search-options select input {
  border: none !important;
}


/** Style page **/
#style-show { padding: 5px 0 !important; }
#style-show > * { float: left; width: 97.8% }
#obsoletion-message {
  position: relative;
  background-color: #ffec8e !important;
  border: 1px solid #ffd400 !important;
  -moz-border-radius: 0 !important;
  padding: 1% !important;
  margin: 0 !important;
  width: 48% !important;
  height: 120px;
}
#obsoletion-message + #install { display: none }
#install {
  position: relative;
  background-color: #dbff7b !important;
  border: 1px solid #8fc665 !important;
  -moz-border-radius: 0 !important;
  padding: 1% !important;
  margin: 0 !important;
  width: 48% !important;
  height: 120px;
}
#install > * { padding: 0 !important; }
#install > h2 {
  font-size: 18pt !important;
  padding: 0 !important;
  margin: -5px 0 0 !important;
  color: #267f00 !important;
  font-style: normal !important;
}
#install > div > p { margin: 2px 0; }
#style-meta {
  position: absolute;
  margin: 0 ;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
  width: 47%;
  height: 120px;
}
#style-meta > * {
  margin: 3px 0;
}
#donate {
  margin: 8px 0 0;
  display: block !important;
}
#long-description {
  margin-top: 5px;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
}
#style-options {
  margin-top: 5px;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
}
#style-options h3 {
  margin: 0 0 5px !important;
  padding: 0 !important;
  background: none !important;
  border: none !important;
}
#style-options fieldset {
  border: 1px solid #1e79a7;
}
#screenshots {
  margin-top: 5px;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
}
.screenshot-box { display: block !important }
.screenshot-caption { font-style: normal !important; }
.screenshot {
  margin-top: 5px;
  border: 2px solid #EEE !important;
  -moz-box-shadow: 0px 2px 4px rgba(0,0,0,.88);
}
#screenshots img:hover {
  max-width: none !important;
  max-height: none !important;
  height: auto !important;
  width: auto !important
}
#show-code {
  margin-top: 5px !important;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
}
#show-code #view-code {
  margin: 1% 0 0 !important;
  padding: 1% !important;
  border: 1px solid #bbb !important;
  border-left-width: 3px !important;
}
#show-code #stylish-code {
  font: 8pt Consolas !important;
}
#style-show #show-code + h3 {
  margin: 5px 0 0;
  padding: 1% !important;
  background: #cfecff;
  border: 1px solid #abdafb;
  border-bottom: none;
}
#discussions {
  list-style-position: inside;
  margin: 0 0 10px 0 !important;
  padding: 1% !important;
  background: #cfecff;
  border: 1px solid #abdafb;
  border-top: none;
}
#post-discussion {
  padding: 0 1% !important;
  background: #cfecff;
  border: 1px solid #abdafb;
  border-top: none;
  border-bottom: none;
}
#add-review {
  border: 1px solid #abdafb;
}
#no-discussions {
  margin: 0 0 10px 0 !important;
  padding: 1%;
  background: #cfecff;
  border: 1px solid #abdafb;
  border-top: none;
}


/** User Page **/
/*body > h1 + ul {
  position: absolute;
  right: 0;
  margin: 0 !important;
  padding: 6px 8px 4px !important;
  background: #38abe3 !important;
  -moz-border-radius: 0 0 5px 5px;
}
body > h1 + ul li {
  display: inline;
  margin: 0 !important;
}
body > h1 + ul li a {
  position: absolute;
  margin-left: -22px;
  opacity: 0;
}*/

body > h1 + ul { margin-top: 15px !important; }
body > h1 + ul li {
  list-style: none inside none !important;
  padding: 4px 0 4px 25px; 
  background-position: left 3px;
  background-repeat: no-repeat;
}

body > h1 + ul li:nth-child(1) { /* create new style */
  background-image: url(data:image/png;base64,);
}
body > h1 + ul li:nth-child(2) { /* change details */
  background-image: url(data:image/png;base64,);
}
body > h1 + ul li:nth-child(3) { /* change pwd */
  background-image: url(data:image/png;base64,);
}
body > h1 + ul li:nth-child(4) { /* migrate to openid */
  background-image: url(data:image/png;base64,);
}
body > h1 + ul li:nth-child(5) { /* log-out */
  background-image: url(data:image/png;base64,);
}
body > h1 + ul li:nth-child(6),
body > h1 + ul li:only-child { /* discussions */
  background-image: url(data:image/png;base64,);
}

body > h1 + ul li a { text-decoration: none !important }

body > h1 + ul + h2 {
  margin: 15px 0 0 !important;
  padding: 5px 10px !important
}
.subtitle { margin: 5px 0; }

#style-table { margin: 15px auto 5px !important; }
#style-table th {
  background: #bbe2f7 !important;
  border: 1px solid #abdafb !important; 
  color: #1e79a7 !important;
}
#style-table th .diffP { color: green !important }
#style-table th .diffN { color: red !important }
#style-table tr:hover td { background: #cfecff !important; }
#style-table td { border: 1px solid #abdafb !important; }

#style-table .metainfo span, #style-table .subtitle { font-size: 80% !important; }

#style-table .edit {
  background: url(data:image/png;base64,) center no-repeat !important;
}
#style-table .delete {
  background: url(data:image/png;base64,) center no-repeat !important;
}
#style-table .metalink {
  background: url(data:image/png;base64,) center no-repeat !important;
}
#style-table .metascreens {
  background: url(data:image/png;base64,) center no-repeat !important;
}
#style-table .metadiscussions {
  background: url(data:image/png;base64,) center no-repeat !important;
}
#style-table .loading {
  background: url(data:image/png;base64,) center no-repeat !important
}



/* Pagination
  ------------ */
.pagination {
  margin: 20px 0 10px;
  text-align: center;
}
.pagination > * {
  padding: 4px 6px;
  color: #1e79a7 !important;
  background: #bbe2f7;
  border-top: 1px solid #abdafb;
  border-bottom: 1px solid #abdafb;
}
.pagination .prev_page {
  font-weight: bold;
  -moz-border-radius: 3px 0 0 3px;
  border-left: 1px solid #abdafb;
}
.pagination .disabled {
  color: #AAA !important;
  font-weight: normal !important;
}
.pagination .next_page {
  font-weight: bold;
  -moz-border-radius: 0 3px 3px 0;
  text-decoration: none !important;
  border-right: 1px solid #abdafb;
}
.pagination .current {
  background: #FFF !important
}
.pagination [href], .pagination .current, .pagination .gap {
  margin-left: -3px;
}

} /* end domain(userstyles.org) */

/*
+-----------------+
| +-------------+ |
| | F O R U M S | |
| +-------------+ |
+-----------------+
 */

@-moz-document domain("forum.userstyles.org") {

#Head .Menu h1 { font-size: 10pt !important }

.Comment .Message { margin: 8px !important }

code {
  -moz-border-radius: 0 !important;
  border: 1px solid #ffe375 !important;
  border-left: 4px solid #FFD722 !important;
  margin: 3px 0 !important;
}

pre {
  -moz-border-radius: 0 !important;
  border: 1px solid #CCC !important;;
  border-left: 4px solid #999 !important;
  background: #FFE !important;
  margin: 3px 0 !important;
}

blockquote {
  background: #BBE2F7;
  border: 1px solid #abdafb !important;
  border-left: 4px solid #1e79a7 !important;
  margin: 3px 0 !important;
  padding: 5px !important;
}

.Message .emoticon { vertical-align: middle !important }

} /* end domain(forum.userstyles.org) */
