css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://yaplog.jp/benijake148/") {
body{
	background-color:#FFCFCF!important;
}
#wrapper{
	position:relative!important;
}
#header h2{
	color:#4F4F4F!important;
}
/*リンク色*/
/*本文中のリンク*/
.entry_main a{
	color:#FF630F!important;
}
.entry_main a:hover{
	color:#FF6FA1!important;
}
/*上部ページナビと下部のこの記事のurl*/
#main .pagenavi01 a
,.entry_posted a
,.entry_ping a{
	color:#7F4807!important;
	text-decoration:none!important;
}
#main .pagenavi01 a:hover
,.entry_posted a:hover
,.entry_ping a:hover
,#main .pagenavi01 a[href="http://yaplog.jp/benijake148/"]:hover{
	color:#FF7FAB!important;
	text-decoration:underline!important;
}
/*Main*/
#main .pagenavi01{
	color:#2F2A1D!important;
}
#main .pagenavi01 a[href="http://yaplog.jp/benijake148/"]{
	color:#5F5F5F!important;
 }
#side01 a:hover{
	color:#FF6FA1!important;
	text-decoration:none!important;
	text-decoration:underline!important;
} 
#side02 a:hover{
	color:#FF6FA1!important;
}
/*ピンク部分*/
#whole{
	background-repeat:no-repeat!important;
	background-color:#FFDFDF!important;
	width:1024px !important;

	margin:0px auto!important;
	padding-bottom:15px!important;

	/*border:1px solid #FFDFDF!important;
	border-top:none!important;*/

	/*border-radius:0px 0px 30px 30px !important;*/
	-moz-border-radius:0px 0px 30px 30px !important;
	-webkit-border-radius:0px 0px 30px 30px !important;
	-o-border-radius:0px 0px 30px 30px!important;

	-moz-box-shadow: 0px 5px 10px rgba(207,168,168,0.2);
	-webkit-box-shadow: 0px 5px 10px rgba(207,168,168,0.2);
	-o-box-shadow: 0px 5px 10px rgba(207,168,168,0.2);
}

#main .pagenavi01{
	margin-bottom:-25px!important;
}
#main00{
	margin-top:-10px!important;
	min-height:1300px !important;
}
#side01,
#side02 {
	margin-top:20px!important;
}
/*記事部分*/
#main{
	color:#6F6F6F!important;
	position:relative!important;
	z-index:10!important;

	/*border-radius:30px 30px 30px 30px !important;*/
	-moz-border-radius:30px 30px 30px 30px !important;
	-webkit-border-radius:30px 30px 30px 30px !important;
	-o-border-radius:30px 30px 30px 30px!important;

	-moz-box-shadow: 0px 5px 20px rgba(207,168,168,0.5);
	-webkit--box-shadow: 0px 5px 20px rgba(207,168,168,0.5);
	-o-box-shadow: 0px 5px 20px rgba(207,168,168,0.5);
}

/*記事*/
.entry_title h4{
	font-size:15px !important;
	line-height:1.2!important;
	font-weight:normal!important;
	color:#4F3F2C!important;

	position:relative!important;
	top:3px!important;
	left:0px!important;

}
.entry_title{
	color:#7CAF62!important;
}
.entry_main{
	font-size:14px !important;
	line-height:1.4!important;
	color:#4F4836!important;
	
}
.entry_main span[style="font-size: 125%;"]{
	font-size:14px !important;
	
}
/*大きい画像の場合枠内に収める*/
html body div#whole div#wrapper div#main div#main00 div.entry div.entry_main span a img
,html body div#whole div#wrapper div#main div#main00 div.entry div.entry_main span img{
	max-width:360px !important;
}

/*記事終りのPR*/
div[style="margin: 6px 0pt 15px; padding: 0pt; text-align: right;"]{
	display:none !important;
}
/*画像終りのライン（記事下部角丸だった部分）*/
#main_under{	
	background-image:none!important;
	background-color:#FFCFCF!important;
	width:1024px!important;
	height:2px !important;

	position:absolute!important;
	top:819px!important;
	left:-18.2%!important;
	z-index:1!important;
}

/*記事エリア下部を角丸に*/
#whole background-image{
	border:1px solid #666666!important;
}

/*記事上の角丸含むサイド部分*/
.pagenavi01{
	padding-bottom:20px!important;
}

/*読者になるバナー*/
div#widgetProfile.widget div.side div.side_text div[style="text-align:center;"]{
	margin-top:5px!important;
	margin-left:-25px!important;
	margin-bottom:-10px!important;
}
div#widgetProfile.widget div.side div.side_text div a{
	background-image:none!important;
}
div#widgetProfile.widget div.side div.side_text div a:hover{
	opacity:0.7!important;
}
div#widgetProfile.widget div.side div.side_text div a img.mdwf266_0{
	max-width:140px !important;
}

/*---------------------------------------サイド部分------------------------------*/
#side01{
	text-align:center !important;
}
#side01 a{
	padding-left:0px!important;
}
.side .side_title{
	color:#4F4F4F!important;
}
/*カレンダー*/
#side01 #widgetCalendar .calenderPage{
	padding-right:0px!important;
}
#side01 #widgetCalendar a{
	color:#EF2C2C!important;
	text-decoration:none!important;
}
#side01 #widgetCalendar a:hover{
	color:#FF0F0F!important;
	background-color:#FFCFCF!important;

}
#side01 #widgetCalendar .calenderPage a{
	background-image:none!important;
}
/*---------------------新しい独り言------------------------*/
#side01 #widgetEntries .side_text{
	color:#5F5F5F!important;
	font-size:11px !important;
	line-height:1.2 !important;
	/*border-bottom:1px solid #EFEFEF!important;*/

	padding:10px 0px!important;
	margin-bottom:10px!important;
}
/*#side01 #widgetEntries .side_text a{
	text-decoration:none!important;
}*/

div#side01 div#widgetEntries.widget div.side div.side_text div.icon a:visited{
	color:#7F7F7F!important;
}

div#side01 div#widgetEntries.widget div.side div.side_text div.icon a:hover{
	color:#FF6FA1!important;
} 
/*---------------------------twitter------------------------*/
div#twtr-profile-widget.twtr-widget{
	position:absolute!important;
	top:450px!important;
	right:12px!important;
}
/*マーク*/
#side01 #widgetEntries .side_text .icon {
	padding-left:1.2em!important;
	position:relative!important;
}
#side01 #widgetEntries .side_text .icon:before {
	content:"★"!important;

	position:absolute!important;
	top:0px!important;
	left:1px!important;
}
#side01 #widgetEntries .side_text .icon a{
	background-image:none!important;
}

div#twtr-profile-widget.twtr-widget div.twtr-doc div.twtr-hd
,div#twtr-profile-widget.twtr-widget div.twtr-doc div.twtr-hd h4 a
,div#twtr-profile-widget.twtr-widget div.twtr-doc div.twtr-bd
,div#twtr-profile-widget.twtr-widget div.twtr-doc div.twtr-ft{
	background-color:#FFBFBF!important;
	background-color:#E97A8A!important;
	background-color:#EF95A3!important;
}

/*左カラム広告*/
#side01 iframe:nth-of-type(2)
,#side01 iframe:nth-of-type(3)
,#side01 iframe:nth-of-type(4)
,#side01 iframe:nth-of-type(6)
,#side01 iframe:nth-of-type(7)
,#side01 iframe:nth-of-type(8)
,#side01 iframe:nth-of-type(9)

,#side01 iframe:nth-of-type(1)
,#side01 iframe:nth-of-type(5){
	display:none !important;
}

.side_text{
	padding-top:10px!important;

}
/*-------------------小明のオシゴト&ファミリー--------------------*/
/*～小明BBS～*/
#side02 #widgetLink .side .side_text .icon:first-child a{
	padding:0px 0px!important;
}
#side02 #widgetLink .side .side_text .icon:first-child a:before{
	content:"★"!important;
}
#side02 #widgetLink .side .side_text .icon:first-child a:after{
	content:"★"!important;
}
#side02 #widgetLink .side .side_title:nth-of-type(1){
	font-size:11px !important;
}
#side02 #widgetLink .side a:nth-of-type(1){
	color:#5F5F5F!important;
	font-size:11px !important;
	line-height:1.2 !important;

	background-image:none!important;
	padding:0px!important;
}
#side02 #widgetLink .side a:nth-of-type(1):hover{
	color:#FF7FAB!important;
	text-decoration:none!important;
}


/*------------------------プロフィール-----------------------------*/
#side02 #widgetProfile{
	/*width:160px !important;
	position:absolute!important;
	top:600px!important;
	left:0px!important;*/

	margin-top:350px !important;
}
#side02 #widgetProfile .side .side_text{
	color:#5F5F5F!important;
	font-size:11px !important;
	line-height:1.2 !important;
}
/*写真*/
#side02 #widgetProfile .side .side_text ul li.image img{
	outline:1px solid #EFEFEF!important;
}
/*ニックネーム*/
#side02 #widgetProfile .side .side_text ul li.icon{
	font-size:10px !important;
	line-height:1.2 !important;
	padding:1px 0px!important;
}
#side02 #widgetProfile .side .side_text ul li.free{
	line-height:1.4 !important;
	padding:0px 0px!important;
	margin-bottom:0px!important;
}
#side02 #widgetProfile .side .side_text ul li.free br+br+br{
	height:500px !important;
}
/*プロフィールページを見る*/
.sideProfMenu a{
	color:#5F5F5F!important;
	background-image:none!important;
	padding:0px 0px!important;
}
/*-------------------------月別アーカイブ-----------------------*/
#widgetMonthly{
	width:160px !important;
	position:absolute!important;
	top:900px!important;
	left:3px!important;
	border-bottom:1px solid #EFEFEF!important;
}	
#widgetMonthly .side .side_text{
	height:30em !important;
	overflow:auto!important;
}
/*-------------------------日別アーカイブ-----------------------*/
#widgetDaily{
	width:160px !important;
	position:absolute!important;
	top:600px!important;
	left:3px!important;
	/*border-bottom:1px solid #EFEFEF!important;*/
}
#widgetDaily .side .side_text{
/*	height:25em !important;
	overflow:auto!important;*/
}
/*-------------------------カテゴリアーカイブ---------------------*/
#widgetCategories{
	width:160px !important;
	position:absolute!important;
	top:1270px!important;
	left:3px!important;
}

/*--------------------買い物---------------------------*/
#widgetCategories+table{
	display:none !important;
}
/*
#widgetCategories+table .side{
	overflow:hidden!important;
	text-align:center!important;
}
#widgetCategories+table .side .side_text2{
	overflow:hidden!important;
}
#widgetCategories+table .side table .side_title{
	padding-bottom:15px!important;
}
#widgetCategories+table .side table +div+table{
	padding-top:10px!important;
}
#widgetCategories+table .side table +div+table .side_text2{
	width:154px!important;
	padding:0!important;
	margin:0!important;
}*/

/*コピーライト*/
#footer{
	padding-top:35px!important;
	text-align:center!important;
}
#footer img{
	opacity:0.5!important;
}
/*広告削除*/
.keywd_ad{
	display:none !important;
}
/*一番上のヤプログバー削除*/
body{
	padding:0!important;
}
#common_head{
	width:1024px !important;
	position:absolute!important;
	top:-99999em!important;
	left:0em!important;
}
#logo_common_head{
	position:absolute!important;
	top:99999em!important;
	left:0!important;
	background-color:#FFCFCF!important;
	opacity:0.5!important;
}
#logo_common_head a:after{
	content:"yaplog!" !important;
	font-size:14px !important;
	color:#FFCFCF!important;
}
#logo_common_head img{
	display:none !important;
}
}

@-moz-document url-prefix("http://yaplog.jp/benijake148/image/") {
/*一番上のヤプログバー*/
body{
	padding:0px!important;
}
#common_head{
	width:auto !important;
	position:relative!important;
	top:0em!important;
	left:0em!important;
}
#logo_common_head{
	position:relative!important;
	top:0em!important;
	left:0!important;
	background-color:#FFFFFF!important;
	opacity:1!important;
}
#logo_common_head a:after{
	content:"" !important;
}
#logo_common_head img{
	display:inline!important;
}
#wrapper{
	padding-top:20px!important;
}
#mainL {
	padding-bottom:0px!important;
}
#mainL .imgMain img{
}
/*ピンク部分*/
#main{
	-moz-box-shadow: 0px 0px 0px rgba(207,168,168,0.5);
	-webkit--box-shadow:0px 0px 0px rgba(207,168,168,0.5);
	-o-box-shadow: 0px 0px 0px rgba(207,168,168,0.5);
}
.mainRBox
,#footer{
	background-color:#FFFFFF!important;
}

html body#pageImage div#wrapper.column table#main{
	/*margin-bottom:50px!important;*/
}
/*広告削除*/
html body#pageImage div#wrapper.column div.headerAds
,html body#pageImage div#wrapper.column table#main tbody tr td div.ads{
	display: none!important;
}
}
