css:
@-moz-document url-prefix(http://www.google.com/search),
               url-prefix(http://www.google.com/swr),
               url-prefix(http://www.google.com/advanced_search),
               url-prefix(http://www.google.com/language_tools),
               url-prefix(http://www.google.com/preferences),
               url-prefix(http://www.google.com/custom) {

/* --- Modifications for GooglePedia --------------------------------------- */
  div#wikibox {
    background:transparent !important;
    border-top:solid 1px white !important;
    border-bottom:solid 1px white !important;
    padding:3px !important;
  }

  div#wikibox * {
    font-size:11pt !important;
    line-height:normal !important;
  }

  div#wikibox h1, div#wikibox h1 * {
    font-size:18px !important;
    font-weight:bold !important;
  }

  div#wikibox h2, div#wikibox h2 * {
    font-size:14px !important;
    font-weight:bold !important;
  }

  div#wikibox h3, div#wikibox h3 * {
    font-size:12px !important;
    font-weight:bold !important;
  }

  div#wikibox h4, div#wikibox h4 * {
    font-size:11px !important;
    font-weight:bold !important;
  }

  div#wikibox h5, div#wikibox h5 * {
    font-size:10pt !important;
    font-weight:bold !important;
  }

  div#wikibox h3#siteSub {
    font-size:9pt !important;
    font-weight:bold !important;
  }

  div#wikibox h1, div#wikibox h2, div#wikibox h3, div#wikibox h4, div#wikibox h5, div#wikibox h1 span, div#wikibox h2 span, div#wikibox h3 span, div#wikibox h4 span, div#wikibox h5 span {
    color: white !important;
  }

  div#wikibox.shrunk {
    border-left:solid 3px white !important;
  }

  div#wikibox .thumb {
    background:#202020 !important;
    border:solid 1px #202020 !important;
  }

  div#wikibox .tright, div#wikibox .tleft/*, div#wikibox .noprint*/, div#wikibox #ograniczenia, div#wikibox #Vorlage_Alternative, div#wikibox #catlinks, div#wikibox .infobox, div#wikibox .toccolours, div#wikibox [style*="background: rgb(249, 249, 249)"] {
    background:transparent !important;
    border:solid 1px white !important;
  }

  div#wikibox .infobox caption {
    color: #a0a0a0 !important;
  }

  div#wikibox .thumb *, div#wikibox [bgcolor="#efefef"] {
    background:#303030 !important;
    border: 0px !important;
  }

  div#wikibox .navHead, div#wikibox [style*="background-color: lightblue"], div#wikibox [style*="background: rgb(204, 204, 255)"] {
    background: #304040 !important;
    color: #a0a0a0 !important;
    font-weight: bold !important;
  }

  div#wikibox div[style*="background: rgb(255, 255, 221)"] {
    background:#202000 !important;
    border:solid 1px #804000 !important;
  }

  div#wikibox [style='text-align: center; background-color: rgb(229, 229, 229);'] {
    background:#202020 !important;
  }

  div#wikibox #toc {
    background:transparent !important;
    border:solid 1px lightgrey !important;
  }

  div#wikibox #toc * {
    font-size:10pt !important;
  }

  div#wikibox #toc h2 {
    font-size:12pt !important;
  }

/* --- End of GooglePedia Modifications ------------------------------------ */


}
