css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gaiaonline.com") {
#gaia_header .gaia_menu_channel{ background:url(http://i19.photobucket.com/albums/b160/tenkabuto/fillnopercent.gif) repeat-x top left !important; }
#gaia_header #gaia_menu.yuimenubar{ background:url(http://i19.photobucket.com/albums/b160/tenkabuto/fillnopercent.gif) repeat-x center !important; }
#gaia_header div.yuimenu li,
#gaia_header div.yuimenubar li{
    font-size:92%;
    font-weight: normal !important;
}
#gaia_header #gaia_menu.yuimenubar li a{
    color:#787878 !important;
    text-decoration:none;
}
#gaia_header #gaia_menu.yuimenubar li a:hover{
    color:#444 !important;
    text-decoration:none;
}
#gaia_header #gaia_menu.yuimenubar li li a{
    color:#000 !important;
}
#gaia_header #gaia_menu div.yuimenu li.selected{
    background: none !important;
}
#gaia_header div#gaia_menu.yuimenubar li.selected{
    background: none !important;
}
#gaia_header #gaia_menu.yuimenubar .yuimenubaritem{
	padding:0px 10px 0 10px !important;
	height:12px !important;
}
.gaiarchlistitem
{
	background: none !important;
	cursor:pointer;
}
}
