css:
@-moz-document domain('facebook.com') {

/*
Make FB Better by @lookatme (twitter)
Edit for -moz/-o if you like, but please give credit.
Enjoy.
*/

.uiSideNavCount{background-color: #ffffff! important;border: 2px solid rgba(109,132,180,0.5)!important;-webkit-border-radius:15px !important;-moz-border-radius:2px;
	padding: 0 4px 1px !important;	position: relative;
	bottom: 2px;
}

.uiSideNavCount .countValue{color: rgba(109,132,180,0.9)!important;}

#pagelet_pokes{margin-top:20px !important; display: inline-block !important;}

.uiProfilePhotoLarge{-webkit-border-radius:7px !important;}

#rightCol{padding: 0px!important; position:relative; bottom:15px;height:0px}

.home_right_column{padding: 0px!important; height: 0!important;}

#profile_view_actions{padding-bottom: 25px;}

#pageNav {z-index: 10!important;}

.ego_column{position:relative; top:38px;}

div.ego_section{display:none;}

#pagelet_ego_pane{height:100%;}

.uiSideHeader{background: -webkit-gradient(
		linear,
		right bottom, left top,
		from(#F2F2F2),
		to(#FFFFFF)
	)!important;}

span.messageBody{font-size:13px!important;}

.actorDescription {border-bottom:1px dotted rgba(109,132,180,0.3); background: -webkit-gradient(
		linear,
		left top, left bottom,
		from(#FFFFFF),
		to(#FCFCFC)
	); padding-bottom: 0px!important; margin-bottom: 4px!important;}

.actorDescription a:hover {color:#3B5998!important; text-decoration:none!important;}

.actorDescription:hover {color:#3B5998!important; border-bottom:1px solid #cccccc!important;}

#leftCol .hoverZoomLink {-webkit-border-radius: 5px;}

.profile-picture .photo {-webkit-border-top-left-radius: 5px; -webkit-border-bottom-left-radius: 5px; border-left: 1px solid #B3B3B3; border-top: 1px solid #B3B3B3; border-bottom: 1px solid #B3B3B3;}

.uiUfiLike {-webkit-border-radius: 5px;}

.uiUfiViewAll {-webkit-border-top-left-radius: 5px; -webkit-border-top-right-radius: 5px;}

.uiUfiAddComment {-webkit-border-bottom-left-radius: 5px; -webkit-border-bottom-right-radius: 5px;}

.commentList li:first-child {-webkit-border-top-left-radius: 5px; -webkit-border-top-right-radius: 5px;}

.ego_section {visibility: hidden;}

#pagelet_mutual {position: relative; bottom: 40px;}

#c4de51e283488b3645006838 {visibility:hidden;}

/* Fixes for chat (best I can do save from actually just removing the whole thing...) */

div.body {display:none;}
div.mvs.detail {display:none;}
.fbDockWrapper.bb {display:none;}

/* Fixes for the new-fangled message system */

.MessagingReadHeader {padding-bottom: 13px;}

.uiSplitPic.leftPic {-webkit-border-top-left-radius: 7px; -webkit-border-bottom-left-radius: 7px;-webkit-border-top-right-radius: 0px!important; -webkit-border-bottom-right-radius: 0px!important;}

.uiSplitPic.leftThree {-webkit-border-top-left-radius: 7px; -webkit-border-bottom-left-radius: 7px;}

.uiSplitPic.rTop {-webkit-border-top-right-radius: 7px;}

.uiSplitPic.rBottom {-webkit-border-bottom-right-radius: 7px;}

.UIImageBlock_MED_Image {border-radius:7px;}

.UIImageBlock_SMALL_Image img {border-radius:7px;}

.heavySeparator {border-top: 1px dashed #AAA!important;}

.uiHeaderActions.rfloat .uiToolbarItem {float: right !important; position: relative !important; bottom: 10px; margin-bottom: 2px !important;}

.uiHeaderWithImage {padding-bottom: 2px !important;}}
