css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cap123.blog11.fc2.com") {

body{
	margin-top:-38px!important;
}
a:link{
	color:#3F8EFF!important;
	text-decoration:none!important;
}
a:visited{
	color:#9B9FCF!important;
	text-decoration:none!important;
}
a:hover{
	color:#FF833F!important;
	text-decoration:underline!important;
	text-decoration:none!important;
}
#header_body h1 a{
	color:#FFFFFF!important;
}
#header_body h1 a:hover{
	color:#DFDFDF!important;
	text-decoration:none!important;
}
h2
,b{
	font-weight:normal !important;
}
.menu{
	background-color:#DFDFDF!important;
}
.menu a{
	color:#4F4F4F!important;
}
.menu a:hover{
	color:#FF833F!important;
}
/*左カラム*/
.plugin-calender{
	padding-bottom:0.5em!important;

}
.plugin-calender+div{
	height:20em !important;
	overflow:auto!important;

	border:1px solid #DFDFDF!important;
	border-right:0px solid #DFDFDF!important;
	border-left:0px solid #DFDFDF!important;
	padding-left:0.2em!important;
}
#left .plugin_outline:nth-of-type(4){
	margin-top:-1em!important;
	margin-bottom:1em!important;

}
#left .plugin_outline:nth-of-type(4) .plugin_body{
	height:40em !important;
	overflow:auto!important;

	border:1px solid #DFDFDF!important;
	border-right:0px solid #DFDFDF!important;
	border-left:0px solid #DFDFDF!important;
}


/*右カラム*/
#right .plugin_outline:nth-of-type(1) table td[bgcolor="#dcdcdc"]{
	background-color:#EFEFEF!important;
}
#right .plugin_outline:nth-of-type(1) table tr:nth-of-type(2)
,#right .plugin_outline:nth-of-type(1) table tr:nth-last-of-type(3){
	display:none !important;
}

