,#right .plugin_outline:nth-of-type(9)
,#right .plugin_outline:nth-of-type(10)
,#right .plugin_outline:nth-of-type(11)
,#right .plugin_outline:nth-of-type(2) table tr.wareclass_row:first-child
,.page_navi ins

,html body div#sh_fc2blogheadbar
,div#outline center div#center-left div#center div.ently_outline div.ently_body div.ently_text div#___plusone_0+p
,div#outline center div#footer_outline div#footer_body span#ad1{
