css:
@-moz-document 
url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader),
url-prefix(http://www.google.com/reader/shared),
url-prefix(https://www.google.com/reader/shared){

.entry-container{line-height:23px !important;background:#F2F1F0 !important;font-size:14px !important;}
.entry .entry-body, .entry .entry-title, .entry .entry-likers {max-width: 760px !important;}
.entry-title-link{font-size:14px !important;font-weight:normal !important;font-family:arial,helvetica,sans-serif !important;line-height:23px !important;color:black !important;}
.entry-title{font-family:arial !important;}
.entry-author{background:#C4C1BD !important;max-width:650px !important;font-family:Arial !important;}
.entry-likers{background:#F2F1F0 !important;}
.entry-body, .entry-main em, .entry-main i, .entry-annotation-body{font-style:normal !important;line-height:23px !important;}
.entry .entry-actions .email {display:none !important;}
.entry .entry-actions .read-state-unread, .entry .entry-actions .read-state-not-kept-unread {display:none !important;}
.entry-comment-content {line-height:20px !important;}
.entry-via{display:none !important;}

span.entry-source-title {background-color: #fcedb0;color: #000000;border-style: solid;border-width: 1px;}
#entries.list .entry .collapsed , #entries.list .collapsed .entry-secondary{height:2.5ex !important;}
#entries.list .entry .collapsed:hover {background:#FFFFCC !important;border-color:#ffffcc !important;}
#entries.list .collapsed .entry-main .entry-source-title {width: 8em !important;}
.samedir #entries.list .collapsed .entry-secondary {margin: 0px 9em 0px 12em !important;}
.samedir #entries.single-source .collapsed .entry-secondary {margin-left: 2em !important;}

.scroll-tree {font-size:13px !important;line-height:23px !important;font-family:arial !important;}
.lhn-section-footer {font-size:13px !important;}
#friends-tree-item-0-main .lhn-section-secondary {height: 300px !important;}
.unread-count {color: #D90000 !important;font-weight: bold !important;font-family:arial !important;font-style:italic !important;}

#chrome-header {display:none !important;}
#gbar {display:none !important;}
.gbh {display:none !important;}
#stream-prefs-menu {display:none !important;}
#sgr-prefs-menu{display:none !important;}
#viewer-refresh {display:none !important;}
#logo{display:none;}
#viewer-top-controls {margin-top: -65px;}
#lhn-add-subscription-section {margin-top: -40px;}
#guser {text-align: left;padding-left: 20px;}

#search-input, #search-restrict{display:none;}
#search:hover #search-input, #search:hover #search-restrict, #search:hover #search-submit, #search:hover #search-submit{display:block;}
.lhn-hidden #search{left:68px !important;}
#search {position: absolute;top: 28px;}

#viewer-footer {padding: 0 ;margin-top:0 !important;background-color:transparent !important;}
#viewer-footer .goog-button  {display:none !important;}
#entries-status {position: fixed !important;top: 6px !important;left: 400px !important;text-align: left !important;}
#viewer-all-new-links {float: right !important;}
#mark-all-as-read-split-button {float: left !important;}

/*Expanded view*/
div.card-content {padding: 0;}
div.entry-container {margin: 0;padding: 0;}
.cards .entry {padding: 0;margin: 0;}
div.card.card-common {border-style: none;margin: 0;padding: 0;}
div.card-actions.card-bottom {display: none;}


}
