css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

body {background: url('http://24.media.tumblr.com/tumblr_lezs54IusO1qzg4y8o1_500.jpg') center right #333333 repeat fixed !important;}

ol#posts li blockquote,body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;

padding-left: 10px !important;

border-width: 4px !important;

border-color: #000000 !important;}

ol#posts li blockquote blockquote,body.mceContentBody blockquote blockquote {border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote {border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote,body.mceContentBody blockquote blockquote blockquote blockquote 

{border-color: #000000 !important;}

ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #000000 !important;}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background color: #41383C; !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background:#111111 !important;

-webkit-border-radius: 20px;

-moz-border-radius: 20px;

border-radius: 20px;

margin: auto;

border-style: solid;

border-width: 0px !important;

border-color: #000000 !important;

padding: 20px 20px;}

h1 {color: #333333;text-shadow: 0 0 2px #41383C;}

.no_posts_found, h1.dashboard_header {color: #000000;}

img#content_top, img#content_bottom {display: none;}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current 

{background: none;}

#right_column .dashboard_nav_item {background: none repeat-x;background-attachment: initial;

background-position-x: 50%;

background-position-y: 100%;

background-origin: initial;

background-clip: initial;

background-color: initial;}

#right_column .dashboard_nav_item #hide_radar {background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: #333333;}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video 

{background:rgba(0,0,0,0.0);}

#nav .nav_item.active {background: rgba(00,0,0,0.5) !important;}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {background: rgba(00,0,0,0.5) !important;}

ol#posts li.notification.alt {background-color: rgba(0,0,0,0.5);}

ol#posts li.notification.first_notification {background-color: rgba(0,0,0,0.5);}

ol#posts li.notification {background-color: rgba(0,0,0,0.5);

border-bottom: 1px solid #000000;}

ol#posts li.notification.last_notification {

border-bottom: 1px solid #000000;}

ol#posts li.post .permalink {background: rgba(00,0,0,0.5); padding-top:20px;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {background: rgba(0,0,0,0.8);border: solid #000000;}

ol#posts li.post .post_avatar{border-bottom: 0px solid #000000;}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;border-bottom: 1px solid #000000;}

#right_column a#dashboard_switch_blog_menu_current:hover {background: none;}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current 

{background: none;}

form.dashboard_options_form {background-color: rgba(0,0,0,0.0);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-right-radius: 10px 10px;

border-top-right-radius: 10px 10px;

color: #000000;display: block;font-size: 13px;margin-bottom: 25px;padding: 15px 22px;}

form.dashboard_options_form .option_container {border-top: 1px solid #000000;}

.no_posts_found, h1.dashboard_header {color: #333333;}}
