css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xda-developers.com") {

/* Experiment with theming images...  All of the following are
replacing css called images with ones I've modified to be more
blue, which are coming from my personal web server */

#header { /* position: relative; */ height: 67px; background: #53360c url(http://wally.unlimitedphoto.com/xda/blue-headerbg.png) top left !important;}

#navbar { /* position: relative; */  border-top: 1px solid #56401e; height: 40px;
  background: #3d2b11 url(http://wally.unlimitedphoto.com/xda/blue-navbarbg.png) top left repeat-x !important;
}

#hpagination a:hover {
  background-image: url(http://wally.unlimitedphoto.com/xda/blue-paginationabghover.png) !important;
}

#hpagination #current_page {
  background-image: url(http://wally.unlimitedphoto.com/xda/blue-paginationabghover.png) !important;
}

#hpagination a.pagination-prev:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-prev2hover.png) no-repeat !important;
}

#hpagination .pagination-prev2:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-previous3hover.png) no-repeat !important;
}

#hpagination a.pagination-next:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-next2hover.png) no-repeat !important;
}

#hpagination a.pagination-forward:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-forward3hover.png) no-repeat !important;
}

.forumbox-header {
  background: url(http://wally.unlimitedphoto.com/xda/blue-forumbox-headingbg.png) top left repeat-x !important;
  color: #d0e4f2 !important;
}

/* Have to fix the font color here too */
.forumbox-header a {
  color: #d0e4f2 !important;
}

a#slidertoggle { width:225px;
  background: #ffdf4a url(http://wally.unlimitedphoto.com/xda/blue-postbutton.png) bottom left repeat-x !important;
}

#gsc-submit {
  background: url(http://wally.unlimitedphoto.com/xda/blue-search-go.png) !important;
}

#vbnew3 a:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-hepic.png) no-repeat !important;
}

#vbnew a:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-hqpic.png) no-repeat !important;
}

#vbnew2 a:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-hquickpic.png) no-repeat !important;
}

ul.threadcontrols li.postreply {
  background: url(http://wally.unlimitedphoto.com/xda/blue-postreply.png) no-repeat !important;
}

ul.threadcontrols li.postreply a:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-hpostreply.png) no-repeat !important;
}

.qrbutton {
  background: url(http://wally.unlimitedphoto.com/xda/blue-postquickreply.png) no-repeat !important;
}

.qrbutton:hover {
  background: url(http://wally.unlimitedphoto.com/xda/blue-hpostquickreply.png) no-repeat !important;
}

body {
  background: #4a6491 !important;
  color: #85a5cc !important;
  margin: 0 auto !important;
  width: 1200px !important;
}

/* Header when viwing sub-forum list */
.forumheader {
  background: #85a5cc !important
}       

/*  Thread post headers */
.post-header {
  background: #85a5cc !important;
}  

/* Date posted text */
.post-date {
  color: #4a6491 !important;
}

/* Block quotes in posts */
.post-content blockquote {
  background: #85a5cc !important;
}

/* Tags bar at the bottom of posts 4a6491 */
.module-header {
  background: #4a6491 !important;
}

/* Box around thread listings in a forum */
.categorybox {
  background: #d0e4f2 !important;
}

/* Hovering over page numbers in threads, but looks ugly */
#hpagination a:hover {
  color:   #d0e4f2 !important;  
}

/* Not hovering over page numbers in threads */
#hpagination #current_page {
  color: #d0e4f2 !important;
}

/* Not sure!  Look for Purple ;) */
a.cufon {                
  color:   #ffff00 !important;  
}

/* Text in nav links at the top */
#navbar li a {
  color:   #d0e4f2 !important;  
}

/* Text in nav links at the top */
#navbar li a:hover {
  color:   #ffffff !important;  
}

/* USER CP link had to be special..  */
ul#navigation li#nav-last a {
  color:   #d0e4f2 !important;  
}

/* USER CP link had to be special..  */
ul#navigation li#nav-last a:hover {
  color:   #ffffff !important;  
}

/* Color of most links */
a {                
  color:   #384b6d !important;  
  font-weight: bold !important;
}

/* Color of most links 4ab445 */
a:hover {                
  color:   #384b6d !important;  
  font-weight: bold !important;
}

/* Color of post body links 419fb6 */
.post-message a {                
  color:   #419fb6 !important;  
  font-weight: bold !important;
}

/* Color of post body links 4ab445 */
.post-message a:hover {                
  color:   #384b6d !important;  
  font-weight: bold !important;
}

/* Color of text in a post?  Not sure? 4a6491 */
.forumbox .text a {
  color: #ffff00 !important;
}

/* Color of text in a post?  Not sure? 4a6491 */
.view-condensed-item h3 {
  color: #00ff00 !important;  
}

/* Color of text in a post?  Not sure? 4a6491 */
.view-condensed-item h3 a {
  color: #00ff00 !important;  
}

/* Quick Links drop-down text */
ul#navigation li #usercptools_menu td.subitem a {
  color: #4a6491 !important;  
}

/* Color of the author of a post */
.post-author {
  text-shadow: none !important;
}

/* Retweet text on posts */
li.post-options-retweet {
  text-shadow: none !important;
  color: #384b6d !important
}

/* Posting Rules or module header text */
.module-header {
  text-shadow: none !important;
  color: #d0e4f2 !important;  
}

/* Welcome at top of page... */
ul#usernav li {
  padding: 9px 5px; color: #d0e4f2 !important;
}

/* Thread sort etc box at bottom of forum */
.alt1, .alt1Active
{
	background: #d0e4f2 !important;
}

/* editor background */
.panel
{
	background: #d0e4f2 !important;
}

/* editor panel surround */
.panelsurround
{
	background: #85a5cc !important;
}

/* Headers for sections and such in User CP view */
.thead
{
	background: #85a5cc !important;
}

/* Larger headers in the user CP d0e4f2*/
.tcat
{
	background: #d0e4f2 !important;
}

/* User CP Subscribed Threads background */
.tborder
{
	background: #d0e4f2 !important;
}

/* Menus on UserCP on the left d0e4f2 */
.alt2, .alt2Active
{
	background: #d0e4f2 !important;
}

/* Color behind thread listings */
.forumbox {
  background: #eaf6ff !important;
}

/* Color hovering over thread listings */
.threadbit-tr:hover {
  background-color: #d0e4f2 !important;
}

/* Color behind forum listings */
ol.forumlist li {
  background-color: #eaf6ff !important;
}

/* Color hovering over forums  */
ol.forumlist li:hover {
  background-color: #d0e4f2 !important;
}

/* Thin borders around some areas 85a5cc */

.fieldset, .tborder, .forumbox, .post-content, .tcat {
border: 1px solid #add6f2 !important;
}

/* Hide Ads at the top */
#topcontainbox
{
	display: none !important;
}

/* Ads at the bottom */
#ad-billboard-bottom
{
	display: none !important;
}

}
