css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#TabsToolbar {
 height: 28px !important;
 padding-top: 1px !important;
 padding-left: 1px !important;
 background-image: none !important;
}

.tabbrowser-tab {
 min-height: 23px !important;
 margin-top: 2px !important;
 margin-right: 1px !important;
 padding: 0 2px 2px 3px !important;
 background-image: none !important;
 -moz-appearance: tab !important;
 color: ButtonText !important;
}
.tabbrowser-tab[pinned] + .tabbrowser-tab:not([pinned]),
.tabbrowser-tab:not([pinned]):first-child {
 margin-left: 0 !important;
}
.tabbrowser-tab[pinned] {
 padding: 0 1px !important;
}
.tabbrowser-tab .tab-icon-image {
 margin-top: 0 !important;
}
.tabbrowser-tab[pinned] .tab-icon-image {
 margin-top: -1px !important;
}
.tabbrowser-tabs[positionpinnedtabs="true"] .tabbrowser-tab[pinned] .tab-icon-image {
 margin-top: 3px !important;
}
.tabbrowser-tab .tab-text {
 margin-top: 0 !important;
}
.tabbrowser-tab .tab-close-button {
 margin-bottom: -1px !important;
}

.tabbrowser-tab[selected=true] {
 margin-top: 0 !important;
 min-height: 24px !important;
 padding: 0 3px 4px 5px !important;
}
.tabbrowser-tab[selected=true][pinned] {
 min-height: 22px !important;
 padding-right: 3px !important;
 padding-left: 3px !important;
}
.tabbrowser-tab[selected=true] .tab-icon-image {
 margin-top: 1px !important;
}
.tabbrowser-tab[selected=true][pinned] .tab-icon-image {
 margin-top: 1px !important;
}
.tabbrowser-tabs[positionpinnedtabs="true"] .tabbrowser-tab[selected=true][pinned] .tab-icon-image {
 margin-top: 4px !important;
}
.tabbrowser-tab[selected=true] .tab-text {
 margin-top: 1px !important;
}
.tabbrowser-tab[selected=true] .tab-close-button {
 margin-bottom: -2px !important;
}

.tabs-newtab-button {
 margin-top: 2px !important;
 padding-bottom: 3px !important;
 background-image: none !important;
 -moz-appearance: tab !important;
}
