css:
@-moz-document domain('www.spootnik.net'), url-prefix('https://www.spootnik.net/ofocusweb') {
	/* 
		the code in here only apply to:
			-pages on the domain images.example.com
			-pages whose URLs start with http://example.com/images
	*/
}

body {
background-color: black;
color: gray;
font-family: Arial, sans-serif;
font-size: 14px;
margin: 0px;
min-height:100%;
}

.notebook {
background:white;
padding:20px;
padding:20px;
width:760px;
margin-left:auto;
margin-right:auto;
margin-bottom:20px;
margin-top:20px;
min-height:90%;
}

.search {
background:transparent;
position: relative;
z-index: 1;
}

.choosecontext {
background-color: transparent;
margin-left: 5px;
position: relative;
z-index: 1;
}

.switchtodue {
background-color: transparent;
margin-left: 5px;
position: relative;
z-index: 1;
}

.switchtoinbox {
background-color: transparent;
margin-left: 5px;
position: relative;
z-index: 1;
}

.notebookname {
font-weight: bold;
font-size:20px;
left: 0px;
position: relative;
text-align: center;
top: -25px;
width: 100%;
z-index: 0;
text-transform: uppercase;
}

.addproject {
background-color: transparent;
color: white;
left: 650px;
padding: 5px;
position: relative;
top: -45px;
z-index: 1;
}

.addprojectdialog {
background-color: black;
color: white;
display: none;
opacity: 0.9;
padding: 20px;
position: relative;
right: 0px;
z-index: 10000;
}

.projects {
border-bottom: 1px solid gray;
display: none;
padding-top: 20px;
}

.project, .task {
border-top: 1px solid gray;
}

.blankslate {
border-bottom: 1px solid gray;
padding-top: 20px;
}

.notebooktitle {
background-color: gray;
color: white;
height: 30px;
padding: 10px;
}

.addtask {
background-color: white;
color: gray;
padding: 5px;
position: absolute;
right: 10px;
top: 6px;
}

.projectcontent {
color: black;
padding: 10px;
position: relative;
}

input {
color: black;
font-family: Arial, sans-serif;
font-size: 14px;
height: 25px;
margin-left: 0px;
}

.projectname {
display: inline;
font-size: 18px;
font-weight: bold;
text-transform: uppercase;
}

.projectcontent.selected {
background: gray;
color: white;
padding-left: 10px;
padding-right: 10px;
}

.taskcontent.selected {
background: gray;
color: white;
padding-left: 10px;
padding-right: 10px;
}

.addsubtask {
background:none;
bottom: 0px;
color: white;
padding: 5px;
position: absolute;
right: 0px;
}

.choicelist .choiceitem.selected {
background: red;
color: white;
}

.addprojectdialogok {
background: red;
color: white;
margin-left: 5px;
padding: 5px;
}

.choosecontextdialogok {
background: red;
color: white;
margin-left: 5px;
padding: 5px;
}

.searchdialogok {
background: red;
color: white;
margin-left: 5px;
padding: 5px;
}

.taskcontent .buttons {
display: none;
height: 0px;
position: relative;
}

.selected .addtask {
background-color: transparent;
color: white;
}
