css:
/* Android Market - Cleaner & Full Width 
   written by chohdry.com
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("market.android.com") {
	/* Remove background stripes from body and set colour to white */
	body { background: none !important; background-color: white !important;}

	/* Make the content div 100% wide, same as other Google services */
	.doc-content { min-width: 100% !important; width: 100% !important;}

	/* Remove background stripes from app icon/rating area and set colour to dark grey */
	.doc-banner-container {background: none !important; background-color: #494949 !important;}

	/* Remove background stripes from banner area and set colour to green */
	.doc-header-content {background: none !important; background-color: #88B131 !important;}
}
