css:
/*********************************************
@name      Facebook light theme
@namespace Facebook.com
@include   facebook.com/*
@author    Matúš Paulovič (c)
@version   2.0.1
*********************************************/

@-moz-document domain("facebook.com") {

body {
background: url("http://www.maniactive.com/Valentine/all-is-fair.jpg") repeat !important;
}

.uiSideNav .item, .uiSideNav .subitem {
color: maroon !important;
line-height: 15px !important;
border-bottom: 1px solid blue !important;
font-weight: bold !important;
}

#contentCol {
background-color: #CCFFFF !important;
}

.uiHeader h2 {
color: maroon !important;
font-size: 20px !important;
}

.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover, .uiSideNav ul .selectedItem .subitem, .uiSideNav ul .selectedItem .subitem:hover {
background-color: lightgreen !important;
font-weight: bold;
}

.uiMentionsInput .mentionsTextarea, .uiMentionsInput .mentionsTypeahead {
background: #FFEECC !important;
}

#navAccountInfo {
border-bottom: 2px solid brown !important;
}

#navAccount ul {
background: lightgreen !important;
}

#pageLogo a {
background-color: darkblue !important;
}

#blueBar {
background-color: #CCAAFF !important;
}

#headNav {
background-color: #FF7766 !important;
border: 1px solid blue !important;
}

.fbChatBuddylist .fbChatBuddylistError, .fbChatBuddylist .info_text {
background-color: yellow !important;
}

.fbNubFlyoutTitlebar {
background-color: darkblue !important;
border: 1px solid yellow !important;
}

.fbNubFlyout {
background-color: #99EEEE !important;
}

element.style {
background-color: yellow !important;
}

.fbNubButton:hover {
background: lightgreen !important;
}

.fbDock, .fbDockWrapperLeft .lNubContainer, .fbDockWrapperRight .rNubContainer {
background: #CCAAFF !important;
}

.loggedout_menubar_container {
background-color: black !important;
}

}
