css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gamecast-blog.com") {

/* Non-display part */
.footer-outer,
#sub,
#extra,
.article-footer,
ins,
#article-options
{ display: none !important; }

/* Display part */
body,
#container,
.blog-title-outer,
.blog-title-outer-2,
.blog-title-outer-3,
#blog-title,
.blog-description-outer,
#blog-description,
#content,
#main,
.column-inner,
.column-inner-2,
.autopagerize_page_element,
.article-outer-2,
.article-outer-3,
.article-header,
.article-date-outer,
.article-category-outer,
.article-category,
.article-title-outer
{
    width: auto !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

#blog-title
{
    padding-top: 0px !important;
    background: #000000 !important;
}

#blog-title a:link,
#blog-title a:visited,
#blog-title a:visited
{
    color: #ffffff !important;
}

.blog-description-outer,
#blog-description
{
    background: #cccccc !important;
}

.blog-title-outer
{
    min-height: 0px !important;
}

#main
{
    margin-left: 0px !important;
    margin-bottom: 0px !important;
}

.article-date-outer,
.article-category-outer,
.article-category,
.article-category a:link,
.article-category a:visited,
.article-title-outer,
.article-title-outer a:link,
.article-title-outer a:visited
{
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    background: #ccaacc !important;
    color: #000000 !important;
}


}
