css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("simoom.net") {
 * {
  color:#121 !important;
  background-color:#efe !important;
  text-shadow:none !important;
 }
 div#title h1:first-letter {
  color:#121 !important;
 }
 a:link    { color:#33f !important; }
 a:visited { color:#63f !important; }
 a:active  { color:#090 !important; }
 a:hover   { color:#f00 !important; }

 .emps     { color:#565 !important; }

 div#title {
  border-color:#565 !important;
 }
 div#menu ul ul {
  border-top:2px solid #aba !important;
 }
 div#menu li li {
  border-bottom:1px solid #898 !important;
 }
 .col_bg {
  color:#efe !important;
 }
 .red,.red1,.red2 {
  color:#c66 !important;
 }
 .gray,.gray1,.gray2 {
  color:#464 !important;
 }

 input, select, textarea {
  color:#333 !important;
  background-color:#fff !important;
  border:1px solid #333 !important;
 }
 input.btn {
  color:#333 !important;
  background-color:#fff !important;
  border:1px outset #333 !important;
 }
 .dimg {
  display:none !important;
 }
}
