css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* ::::: primary toolbar buttons ::::: */

#nav-bar toolbarbutton:not {#urlbar-reload-button} {
  -moz-appearance: toolbarbutton !important;
  -moz-box-align: center !important;
  -moz-box-pack: center !important;
  margin: 0 !important;
  border: 1px solid transparent !important;
  padding: 3px !important;
  background-color: transparent !important;
  color: -moz-DialogText !important;
}

#nav-bar .toolbarbutton-1 {
  background-color: transparent;
  border-color: ThreeDShadow ThreeDHighlight ThreeDHighlight ThreeDShadow !important;
  box-shadow: none !important;
  text-shadow: 1px 1px ThreeDHighlight !important;
}

#nav-bar .toolbarbutton-icon {
  margin: 1px !important;
  width: 16px !important;
  height: 16px !important;
}

:-moz-any(#back-button, #forward-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-tab-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button, #feed-button, #alltabs-button, #tabview-button) > .toolbarbutton-icon {
  margin: 0 !important;
  width: 18px !important;
  height: 18px !important;
}

#back-button {
  margin: 0 0 0 1px !important;
  -moz-image-region: rect(0, 18px, 18px, 0);
  padding: 2px !important;
}

#forward-button {
  -moz-image-region: rect(0, 36px, 18px, 18px);
  padding: 2px !important;
}

#urlbar,.searchbar-textbox {
  height:26px !important;
  max-height:26px !important;
  margin: -2px 2px !important;
  padding: -2px 2px 0 0 !important;
}

#back-button, #forward-button {
  -moz-appearance: toolbarbutton !important;
  -moz-box-align: center !important;
  -moz-box-pack: center !important;
  margin: 0 !important;
  border: 1px solid transparent !important;
  padding: 3px !important;
  background-color: transparent !important;
  color: -moz-DialogText !important;
}

.urlbar-input-box {
  padding: 2px !important; }
