css:
@-moz-document domain("computerbase.de") {
/***** BACKGROUND IMAGE *****/

html {background: url("data:image/png;base64,") #151515 fixed !important; background-position: center !important}


/***** MAIN CHANGES *****/

body, .tcat, #logo, #footer #footer_exectime, .poll_results dd, .poll_results dd small, ul#article_pages, .tabs, #news-header, ul.content-list li, ol.content-list li, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forumbit_post .forumstats, .forumbit_post .forumstats_2, .threadbit .sticky, .threadbit .nonsticky, .threadbit .discussionrow, .threadbit .alt, .article-related-box, .block1--top::before, .content-list.related-list {background: transparent !important}
.content-list.related-list {column-rule: 1.25em solid transparent !important}
* {background-color: transparent !important; border-color: #333 !important; color: #999 !important}
*:focus {outline: none !important}
::-moz-selection {background: rgba(60,110,255,0.5) !important; color: #CCC !important}
::selection {background: rgba(60,110,255,0.5) !important; color: #CCC !important}

h1, h2, h3, h4, h1 *, h2 *, h3 *, .ticker-group, figcaption, .gist, .mainmenu-nav a, .mainmenu-nav a:visited:not(:hover), .heading, .article__context, .teaser__context, .slide__context, .charts__context, .article-view__context, .teaser__label, .slide__label, .article__label, a:visited .teaser__label, a:visited .slide__label, a:visited .article__label, .teaser-title span, .video-item .teaser-title, .chart_groups td.group_header, .chart-title, blockquote > cite, .bbcode_postedby, .bbcode_postedby strong, div.topnews-emblem, .xf_sel, .thead, .product-title, .group-caption, #raffle_title, .gallery-wrapper strong, .gallery-nav *, .gallery-wrapper .group-caption, .charts div, .charts div small, .gallery strong, #photo_show strong, #fullscreen-gallery .gallery-pics li span, .gallery-fullscreen-button a, .geizhals-items strong, .alt2[style="border: 1px inset;"] strong, .alt2[style="border:1px inset"] strong, #article-teaser-items a.active *, #article-teaser h2, .breadcrumb ul span, .picture_pool_teaser > strong, #pics-nav-label, #ghbody h2 a, #ghbody h3 a, .sskatbox a b, #content_table_hdr .btn3, #content_table_hdr .btn3 *, table.tabular tr th, th.lh1, th.lh1 *, span.notrans > span, span.block_name, table.tborder td.tcat, .pagenav .alt2 strong, .userprof_module a, .as-tabs dd.selected a, .tagnav__a, .tagnav__a:visited, .tagnav__more-toggle, .tagnav__more-toggle:visited, .tagnav__all-link, .tagnav__all-link:visited, span[style="cursor: pointer;"]:hover, td[style="cursor: pointer;"]:hover, th[style="cursor: pointer;"]:hover, .tabs__li a[aria-selected="true"] {color: #DDD !important}
.tabs-li a *, .tabs-li a:visited *, .box--blue time {color: #999 !important}
.chart_groups .comment, .chart_groups .comment * {color: #797979 !important}
.time, .chart-comment, div.news-prev-next, .cl-meta *, .comments-icon-simple, li.tabs-li .disabled, .tabs-li .disabled *, .prev-link.disabled, .next-link.disabled {color: #666 !important}
.pagelinks div.current-page, .page_current, .subnav a.active, .tabs-li .active *, a.xf_sel *, a.xf_msel *, ul.tab_list li.tcat, #posts .userinfo > div > b, span[style="color:red"], span[style="color:red"] *, span[style="color:yellow"] {color: #F70 !important}
#posts font[color] {color: #995 !important}
#posts a font[color] {color: #599 !important}
.chart-carousel__link:not(.chart-carousel__link--enabled) {color: #333 !important}
.bar span, #related_articles span, .figure__subtitle div {color: #FFF !important; text-shadow: 0 0 2px #000 !important}
.box h1, #ghbody h2 a, .lastvisit-separator {text-shadow: -1px -1px #000 !important}
div.topnews-emblem, .date, .nav-quicklinks a {text-shadow: 0 -1px #000 !important}
ol.news-charts li a, .chart-title, .teaser__label, .slide__label, .article__label {text-shadow: 0 1px #000 !important}
.teaser-title {text-shadow: 2px 2px 8px #000, 2px 2px 8px #000, 1px 1px #000 !important}

#usercp_nav hr {background: #2A2A2A !important; box-shadow: 0 1px 0 #141414 !important; border: none !important}

a, a *, .box--blue a, div.cb-backlink a, .threadpagenav .pagination span a, .prev-link:visited, .next-link:visited, .chart-carousel__link:visited {color: #4272DB !important}
a.wikipedia, a.wikipedia:visited {color: #BBB !important; border-bottom: 1px dotted #666 !important}
.text-content p a {color: #BBB !important}
a:visited, a:visited *, ol.news-charts a:visited, .video-item a:visited .teaser-title {color: #777 !important}
a:hover, a:hover *, .text-content p a:hover, .subnav a:hover, .tabs-li a:hover, .tabs-li a:hover *, .video-item a:hover .teaser-title, div.cb-backlink a:hover, .threadpagenav .pagination span a:hover, .prev-link:not(.disabled):hover, .next-link:not(.disabled):hover, .chart-carousel__link--enabled:hover, #login-box a:hover, #login-box a:hover *, .vbmenu_control:hover a, .vbmenu_control:hover a *, .vB_DatePicker tbody td:hover, #article-teaser-items a:hover *, .gh_topmenu h2 a:hover, #ghbody h3 a:hover, .sskatbox a b:hover, ul.tab_list > li.thead:hover, #posts a:hover font[color] {color: #F0F0F0 !important; text-shadow: 0 0 3px #FFF !important}


/***** ANIMATIONS *****/

.google-afc {
  opacity: 0.5;
  transition: opacity 1s}
.google-afc:hover {
  opacity: 1}

.homepage-link, .menu__button--user, .menu__button--search, #pics-logo > a > img, #related_articles img, ul.plain a img.logo, .footer-social-networks a {
  opacity: 1;
  transition: opacity 0.3s}
.homepage-link {transition-property: all !important}
.homepage-link:hover, .menu__button--user:hover, .menu__button--search:hover, #pics-logo > a:hover > img, #related_articles a:hover img, ul.plain a:hover img.logo, .footer-social-networks a:hover {
  opacity: 0.5}

.menu__panel {transition: opacity, visibility, 0.2s}
.menu__panel[hidden] {
  opacity: 0 !important;
  display: block !important;
  visibility: hidden !important;
  transition-duration: 0.6s}

#fullscreen-gallery, .article-footer-toc {
  opacity: 0 !important;
  display: block !important;
  visibility: hidden !important;
  transition: opacity, visibility, 0.7s}
.fullscreen-overlay #fullscreen-gallery, .article-footer-toc.open {
  opacity: 1 !important;
  visibility: visible !important}

.multi-gallery-prev, .multi-gallery-next {opacity: 0.5}
.multi-gallery-prev:hover, .multi-gallery-next:hover {opacity: 1}

.embedded-gallery .gallery-nav, .gallery-fullscreen-button {
  opacity: 0 !important;
  display: block !important;
  transition: opacity 0.7s}
.gallery:hover .gallery-nav, .gallery:hover > .gallery-fullscreen-button {
  opacity: 1 !important}

.newcontent_textcontrol, img[src*="images/buttons/"], #search, #searchbox, .picture_border, .multi-gallery-prev, .multi-gallery-next, ul.content-list a.cl-logo, .pagelinks a {
  transition: opacity, box-shadow, 0.3s !important}


/***** MENUS & POPUPS *****/

/* #login-box > li > * {border: none !important; border-radius: 0 !important;} */
#login-box a, #login-box a * {color: #999 !important}
#login-box > li > a {background: linear-gradient(#2D2D2D, #151515) !important}
#login-box > li > a:hover {background: linear-gradient(#222, #333) !important}
#login-box > li#user-link-li a {border-radius: 0 0 0 10px !important}
#login-box > li:last-child a {border-radius: 0 10px 0 0 !important}
#login-box > li#user-link-li.open > a {
  border-radius: 0 !important;
  background: linear-gradient(#151515, #333) !important}

.menu__panel, .tags-menu {
  background: #222 !important;
  box-shadow: 0 0 8px #000 !important}
.menu__panel::after {
  border-color: currentcolor transparent #222 !important;
  border-width: 0 10px 10px !important}
.menu__panel a {color: #BBB !important}
.usermenu__link {background: #151515 !important}

.vB_DatePicker {box-shadow: 1px 1px 5px #000}

.subnav-sub-ul, .cke_dialog_body, .popupbody {
  background: rgba(20,20,20,.9) !important;
  border-radius: 7px !important;
  box-shadow: 0 0 5px #000 !important}
.subnav-sub-ul a {color: #BBB !important}
.subnav-sub-ul a:hover {
  background: linear-gradient(#333, #151515) !important;
  color: #FFF !important}
.subnav-sub-ul li:first-child a {border-radius: 6px 6px 0 0 !important}
.subnav-sub-ul li:last-child a {border-radius: 0 0 6px 6px !important}

#help_container, #upload_controls_container, .yuimenu .bd {
  background: rgba(20,20,20,.98) !important;
  box-shadow: 0 0 8px #000 !important}

#membermap_popup {
  background: rgba(0,0,0,.8) !important;
  border: 1px solid #111 !important;
  border-radius: 0 7px 7px 7px;
  box-shadow: 2px 2px 8px #000}
#membermap_popup div {
  background: rgba(0,0,0,.3) !important;
  color: #CCC !important;
  border-radius: 0 6px 0 0}
#membermap_popup div * {color: #FFF !important}

#tooltip, .tooltip {
  background: rgba(0,0,0,.8) !important;
  border: 1px solid #151515 !important;
  border-radius: 7px !important;
  color: #CCC !important;
  box-shadow: 2px 2px 8px #000 !important}
#tooltip div, .tooltip div {border-color: #090909 transparent transparent !important}

.tag-filter-menu, span.video-usk18 {background: rgba(0,0,0,.8) !important}

.article-footer-toc, .toc, .menupanel {
  background: rgba(20,20,20,.95) !important;
  box-shadow: 0 0 4px #000, 0 0 40px #777 !important}
.article-footer-toc:before {border-top-color: #333 !important}
.article-footer-toc:after {border-top-color: #171717 !important}

#geizhals-hover {
  background: linear-gradient(#333, #151515) !important;
  padding-bottom: 0 !important;
  border-radius: 6px !important;
  box-shadow: 2px 2px 8px #000}

#Lightbox_overlay {background: #333 !important}
.lightbox > div {
  background: rgba(180,180,180,.2) !important;
  box-shadow: 0 0 12px #000 !important}
.lightbox > div > div {background: #222 !important}

#img_container:hover {background-color: #FFF !important; box-shadow: 0 0 30px #000 !important}

.jwplayer {background: #000 !important}

#ghostery-purple-bubble {
  background: #151515 !important;
  box-shadow: 0 0 12px #000;
  border-radius: 6px !important}


/***** BUTTONS & INPUTS *****/

input, a.cke_dialog_ui_button, .yui_controls .button, .tag-filter-link, .cke_dialog_tab, .buttons > a, .big-button, #homepage-settings button, .chart__dyngroups-selection-button {
  background: linear-gradient(#222, #1A1A1A) !important;
  border: 1px solid #000 !important;
  color: #777 !important;
  text-shadow: 0 -1px 0 #000 !important;
  border-radius: 3px !important;
  box-shadow: inset 1px 1px 0 #333 !important;
  -moz-appearance: none !important}
input:hover, select:hover, a.cke_dialog_ui_button:hover, .yui_controls .button:hover, .tag-filter-link:hover, .buttons > a:hover, .big-button:hover, #homepage-settings button:hover, .chart__dyngroups-selection-button:hover {
  background: linear-gradient(#292929, #1D1D1D) !important;
  color: #999 !important}
input:active, select:active, a.cke_dialog_ui_button:active, .yui_controls .button:active, .tag-filter-link:active, .buttons > a:active, .big-button:active, #homepage-settings button:active, .chart__dyngroups-selection-button:active {
  background: linear-gradient(#1D1D1D, #292929) !important}
input:focus, select:focus {
  background: linear-gradient(#1D1D1D, #292929) !important}
input[disabled], select[disabled], #homepage-settings button[disabled] {
  background: linear-gradient(#222, #151515) !important;
  border: 1px solid #111 !important;
  color: #777 !important;
  opacity: 0.5 !important}
.big-button.usermenu__logout {
  background-image: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIxMiIgaGVpZ2h0PSIxMiIgdmlld0JveD0iMCAwIDEyIDEyIj48cGF0aCBkPSJNMSAxbDEwIDEwTTEgMTFMMTEgMSIgc3Ryb2tlPSIjZmZmIiBzdHJva2Utd2lkdGg9IjMuMiIvPjwvc3ZnPg=="), linear-gradient(#222, #1A1A1A) !important;
  background-position: 9px center, 0 0 !important;
  background-repeat: no-repeat !important}
.big-button.usermenu__logout:hover {background-image: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIxMiIgaGVpZ2h0PSIxMiIgdmlld0JveD0iMCAwIDEyIDEyIj48cGF0aCBkPSJNMSAxbDEwIDEwTTEgMTFMMTEgMSIgc3Ryb2tlPSIjZmZmIiBzdHJva2Utd2lkdGg9IjMuMiIvPjwvc3ZnPg=="), linear-gradient(#292929, #1D1D1D) !important; text-shadow: none !important}
.big-button.usermenu__logout:active {background-image: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIxMiIgaGVpZ2h0PSIxMiIgdmlld0JveD0iMCAwIDEyIDEyIj48cGF0aCBkPSJNMSAxbDEwIDEwTTEgMTFMMTEgMSIgc3Ryb2tlPSIjZmZmIiBzdHJva2Utd2lkdGg9IjMuMiIvPjwvc3ZnPg=="), linear-gradient(#1D1D1D, #292929) !important}

select {
  padding-right: 12px !important;
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAUCAYAAABBECfmAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAxSURBVChTY0AGM2fO/A9logKYBIYCdAE4H5dRuMRHAZUAwYBHV4ChASaAyyQ0CQYGADgSJw2aAl7hAAAAAElFTkSuQmCC), linear-gradient(#222, #1A1A1A) !important;
  background-position: right 4px top -4px, 0 0 !important;
  background-repeat: no-repeat !important;
  border: 1px solid #000 !important;
  color: #777 !important;
  text-shadow: 0 -1px 0 #000 !important;
  border-radius: 3px !important;
  box-shadow: inset 1px 1px 0 #333 !important;
  -moz-appearance: none !important}
select:hover {
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAUCAYAAABBECfmAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAxSURBVChTY0AGM2fO/A9logKYBIYCdAE4H5dRuMRHAZUAwYBHV4ChASaAyyQ0CQYGADgSJw2aAl7hAAAAAElFTkSuQmCC), linear-gradient(#292929, #1D1D1D) !important;
  background-position: right 4px top -4px, 0 0 !important;
  background-repeat: no-repeat !important;
  color: #999 !important}
select:active, select:focus {
  background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAUCAYAAABBECfmAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAxSURBVChTY0AGM2fO/A9logKYBIYCdAE4H5dRuMRHAZUAwYBHV4ChASaAyyQ0CQYGADgSJw2aAl7hAAAAAElFTkSuQmCC), linear-gradient(#1D1D1D, #292929) !important;
  background-position: right 4px top -4px, 0 0 !important;
  background-repeat: no-repeat !important}
select:focus {background-position: right 4px top 12px, 0 0 !important}

input[type="checkbox"] {-moz-appearance: checkbox !important}
input[type="radio"] {-moz-appearance: radio !important}

textarea {
  background: #151515 !important;
  border: 1px solid #000 !important;
  -moz-appearance: none !important}
textarea:hover {background: #1A1A1A !important}
textarea:focus {
  background: #222 !important;
  border: 1px solid #151515 !important}

.spoiler-toggle::before {
  background-color: #202020 !important;
  border: 1px solid #000;
  border-radius: 3px;
  box-shadow: inset 1px 1px 0 #333}

button.promobar__button {background: none !important; border: none !important; box-shadow: none !important}

li.lb-facebook .lb-dummy, li.lb-twitter .lb-dummy {
  background: linear-gradient(#EEE, #BBB) !important;
  border: none !important;
  border-radius: 3px !important;
  box-shadow: inset 0 0 1px #444 !important}
li.lb-facebook .lb-dummy *, li.lb-twitter .lb-dummy * {
  color: #595959 !important;
  text-shadow: 0 1px 0 #EEE !important}
li.lb-twitter .lb-dummy {padding: 2px 3px 0px 3px !important}
li.lb-facebook .lb-dummy {padding: 2px 4px 3px 5px !important}
li.lb-facebook .lb-dummy span {
  font-family: Arial,Sans-Serif !important;
  font-weight: 700 !important;
  letter-spacing: -1px !important}
.lb-dummy {opacity: 0.6}
.active .lb-dummy {opacity: 1}

a.toc-toggle {background: rgba(20,20,20,.9) !important}
.arrow-down:after {border-top-color: #999 !important}

#inlinemod_formctrls .popupctrl, .navpopupmenu.popupmenu.nohovermenu a.textcontrol, .navpopupmenu.popupmenu.nohovermenu a.popupctrl, a.textcontrol {
  background: linear-gradient(#222, #1A1A1A) !important;
  border: 1px solid #333 !important;
  border-radius: 3px !important}

.gallery-nav a, .gallery-nav div, .gallery-fullscreen-button a {
  background-color: rgba(0,0,0,.8) !important}
.gallery-nav a:hover, .gallery-fullscreen-button a:hover {
  background-color: #000 !important;
  box-shadow: 0 0 3px #555 !important}

a.gallery-prev, .picture-view-nav__prev a {border-color: transparent #666 transparent !important; transition: 0.3s}
a.gallery-next, .picture-view-nav__next a {border-color: transparent transparent transparent #666 !important; transition: 0.3s}
a.gallery-prev:hover, .picture-view-nav__prev a:hover {border-color: transparent #CCC transparent !important; text-shadow: none !important}
a.gallery-next:hover, .picture-view-nav__next a:hover {border-color: transparent transparent transparent #CCC !important; text-shadow: none !important}

.newcontent_textcontrol {
  background: #333 !important;
  color: #DDD !important;
  box-shadow: none !important;
  border: 1px solid #000 !important;
  border-radius: 20px !important;
  padding: 1px 9px !important;
  font-weight: bold !important}
.newcontent_textcontrol * {color: #DDD !important}
.newcontent_textcontrol:hover {box-shadow: 0 0 5px #F70 !important}
.newcontent_textcontrol:active {opacity: 0.5 !important}

.pagination span a {background: #191919 !important}
.pagination span.selected a {background: #333 !important}
.threadmeta .pagination span a {background: #222 !important}

img[src="images/buttons/viewpost-right.png"], img[src="images/buttons/lastpost-right.png"], img.gotonewpost {background: url(https://abload.de/img/viewpost_new1m4u.png) no-repeat !important; width: 0 !important; height: 12px !important; padding-left: 12px !important}
img[src="images/buttons/lastpost-right.png"] {height: 9px !important}
img.gotonewpost {height: 11px !important}
a:hover img[src="images/buttons/viewpost-right.png"], a:hover img[src="images/buttons/lastpost-right.png"], a:hover img.gotonewpost {background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAABlSURBVChTYwCB////awCxFRDLQvmyyHwMAJTwBWIQyANimGIQAPOhyhAAKAiyASQJAiAapAGZj1UTyGSSNYEUzQapAAIQG4UPVQYBQAHibQBySPMDUICsUAIpAkuCaGT+oAYMDACPG/UfJ3kRZQAAAABJRU5ErkJggg==) !important}
.toolsmenu .popupmenu a.popupctrl {background-image: url(https://abload.de/img/dropdown1nujlt.png) !important}
.toolsmenu .popupmenu a.popupctrl:hover {background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAHCAYAAADXhRcnAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAABISURBVChTjYzbDQAgCAMZzHHcf4VKPzRIqEhyAfvQAAxnOrwV9Jm79G1w1AfS7wKySOIjB59FkoVY2DtnDpX4VSSl6LRFALYAvLVLFKc3TqsAAAAASUVORK5CYII=) !important}

img[src*="images/buttons/collapse_thead_collapsed.gif"], img[src*="images/buttons/collapse_tcat_collapsed.gif"] {background: url(https://abload.de/img/openg7qz.png) no-repeat !important; width: 0 !important; height: 13px !important; padding-left: 13px !important}
img[src*="images/buttons/collapse_thead.gif"], img[src*="images/buttons/collapse_tcat.gif"] {background: url(https://abload.de/img/closec7y4.png) no-repeat !important; width: 0 !important; height: 13px !important; padding-left: 13px !important}

img[src="images/buttons/sortdesc.gif"] {background: url(https://abload.de/img/sort_up1mrj.png) no-repeat !important; width: 0 !important; height: 12px !important; padding-left: 12px !important}
img[src="images/buttons/sortasc.gif"], img[src="http://b.gzhls.at/sort_down.png"] {background: url(https://abload.de/img/sort_downdmh2.png) no-repeat !important; width: 0 !important; height: 12px !important; padding-left: 12px !important}


/***** MAIN WINDOW *****/

.main .block1 > .wrapper {background: rgba(19,19,19,0.8) !important; box-shadow: 0 0 15px rgba(0,0,0,0.6) !important}

.header {background: rgba(9,9,9,0.9) !important; height: 3.55em !important}
.subheader {background: #18355f !important; padding-bottom: 0 !important}
.subheader::before {background: #0A0A0A !important}

.footer1, .footer2 {background: rgba(9,9,9,0.9) !important}

.block--top, .block1--top, .footer .block1 > .wrapper {background: transparent !important}

.tagnav__more-toggle, .tagnav__all-link {background: rgba(9,9,9,0.8) !important}

.image-overlay {background: rgba(20,20,20,0.8) !important}
.image-overlay .gallery__figure-wrap img {box-shadow: 0 0 20px #000 !important}

#pics-header {box-shadow: 0 3px 3px #000}
#pics-logo {border-radius: 12px 0 0 0 !important}
#pics-header {border-radius: 12px 12px 0 0 !important}
#pics-back-button a {border-radius: 0 12px 0 0 !important}
#pic-title div {color: #000 !important; font-size: 14px !important; text-shadow: 0 -1px 0 #FFF}
#pics-thumbnails li, #pics-overview-button a, #picture_list li {border-radius: 3px !important}
#pics-thumbnails li.highlight, #pics-overview-button.highlight a {border-color: #DDD !important}
#pics-thumbnails li:hover {border: 2px solid #888 !important; margin: 4px 1px !important; padding: 1px !important}
#pics-overview-button a:hover {border: 2px solid #888 !important; margin: 5px 6px !important}
