css:
#main-window[tabsontop="true"]:not([inFullscreen]) 
statuspanel {
  left: auto !important;
  right: 50px !important;
  background: transparent !important;
  opacity: 0.9;
}

#main-window[tabsontop="true"][sizemode="normal"]:not([inFullscreen])
statuspanel {
  top: 105px !important;
}

#main-window[tabsontop="true"][sizemode="maximized"]:not([inFullscreen])
statuspanel {
  top: 85px !important;
}

#main-window[tabsontop="true"]:not([inFullscreen])
statuspanel > .statuspanel-inner {
  height: auto !important;
  border: 1px solid gray !important;
  border-radius: 4px !important;
  background: url("data:image/png;base64,")
  ,-moz-Field !important;
  background-repeat: no-repeat !important;
  background-position: 4px center, 0 !important;
}

#main-window[tabsontop="true"]:not([inFullscreen])
statuspanel > .statuspanel-inner > .statuspanel-label {
  border: 0px transparent !important;
  border-radius: 4px !important;
  background: -moz-Field !important;
  width: 50px !important;
}

#main-window[tabsontop="true"]:not([inFullscreen])
#titlebar {height:32px !important;}
