css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("serienjunkies.org") {
/* re-layout main-area*/
#rap{
 position: absolute;
 left: 20px;
}

/* shift sidebar outside main-area */
#sidebar{
 position: absolute;
 top: 12px;
 left: 102%;
 width: 380px !important
}

/* add some visual effects to sidebar */
#sidebar{
 background-color: rgba(255,255,255,0.98);
 border: 1px solid #000000;
 -moz-border-radius: 4px;
 -moz-box-shadow: 4px 4px 2px #000
}

/* use whole content-area */
#content {
 padding:0px !important;
 width:100% !important
}
#sidebar div,
fieldset.MOD-beschreibung { width: auto !important }

}
