css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org") {

/* Style background and text colors */
header, #install, #left-sidebar nav, #related { 
  background-color: #5573A0 !important;
  color: #FFFFFF !important;
  -moz-border-radius: 10px;
}

/* Style links */
header a, #install a, #related a {
  color: #FFFFFF !important;
  text-decoration: none;
  padding: 3px;
}
#left-sidebar nav ul li a {
  color: #FFFFFF !important;
  text-decoration: none;
}
header a:hover, #install a:hover, #left-sidebar nav ul li a:hover, #related a:hover {
  text-decoration: underline;
}

/* Fix padding for sidebar */
#left-sidebar nav ul li {
  padding-left: 3px;
}
#left-sidebar {
  margin-top: -10px;
}

/* Style header */
header h1 {
  margin-left: 5px;
  margin-top: -3px;
}

/* Style related */
#related h2 {
  color: #FFFFFF;
  padding-bottom: 5px;
  text-align: center;
}

/* Move donate button */
#donate {
  position: absolute;
  top: 12px;
  right: 12px;
}
#donate p, #donate a {
  color: #ffffff;
}
#donate input {
  width: 81px;
  height: 16px;
}

/* Hide ads */
.ad {
  display: none !important;
}

/* Style screenshots */
#screenshots img {
  border-radius: 10px;
  -moz-border-radius: 10px;
}

}
