css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("acidmods.com"){
/*logo*/
body>#maintable+table,
/*ad*/
a[href*="promoclick.aspx"],
/*side ads*/
table[width="100%"][align="center"] center table[width="100%"] td[width="300"] *,
table[width="100%"][align="center"] center table[width="100%"] td[width="127"] *
{display:none!important}

/*resize*/
table[width="100%"][align="center"] center>table[width="90%"],
table[style="width: 630px;"] div[style^="width: 80%"],
table[style="width: 630px;"]{width:100%!important}

/*if you dont want centered text*/
table[style="width: 630px;"] div{text-align:left!important}
}

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://www.acidmods.com/forum/),url-prefix(http://acidmods.com/forum/){
/*logo*/
body>#maintable>table[width="100%"],
/*footer*/
body>table>tbody>tr:not([valign]),
/*side ads*/
body>table>tbody>tr[valign]>td:not([id])
{display:none!important}
}
