css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.com"), domain("lifehacker.com"), domain("kotaku.com"), domain("gawker.com"), domain("deadspin.com"), domain("jezebel.com"), domain("io9.com"), domain("jalopnik.com")
{

#rightcontainer .cn_pager, #hoveringNextPostContainer, #rightbarSpinnerOverlay, #gallery_transition_overlay, #postTransitionOverlay, #rightcontainer .ad_container, #fb_300x250, #right300spinner, .cn_title, .sponsored_splash_post, .ad_marquee
{
    display: none !important;
}

#rightcontainer
{
    right: -350px;
    -moz-transition-duration: .2s;
    -moz-transition-timing-function: linear;
    -moz-border-radius: 5px 0px 0px 0px !important;
}

#date-nav
{
    position: absolute !important;
    left: -50px;
    top: -35px;
    -moz-transition-duration: .2s;
    -moz-transition-timing-function: linear;
    -moz-border-radius: 5px 0px 0px 5px !important;
    border: 1px #888888 solid !important;
}

#toggle
{
    float: left !important;
    margin-right: 10px !important;
}

#rightbar_scroller
{
    margin-top: 35px !important;
}

.inner
{
    width: 959px !important;
}

.gmgrid, .grid-full, .comment, .sitescontainer, .form_part, #postform_widget .header
{
    width: 939px !important;
}

#postform_widget form input[type="text"], #postform_widget form input.cn_image_url, #postform_widget form input.cn_video_url, #postform_widget form textarea, .pager
{
    width: 929px !important;
}

.threadnav, .thread
{
    width: 959px !important;
    margin: 0px !important;
}

.withpic .col-excerpt
{
    width: 610px !important;
}

.col-excerpt
{
    width: 939px !important;
}

#thumb_navigation
{
    width: 959px !important;
}

#thumb_list_window
{
    width: 909px !important;
}

.gallery_image
{
    width: 959px !important;
    height: 540px !important;
}

#gallery_container
{
    width: 959px !important;
    height: 540px !important;
}

#gallery_container img, #gallery_video_container_2 object
{
    max-width: 959px !important;
    height: 540px !important;
    margin: auto !important;
}

.topchrome
{
    padding-right: 330px !important;
}

.abovewrap:not([id="aboveheadlinevideo"])
{
    float: right !important;
    margin: -13px -10px 10px 10px !important;
}

.headline
{
    display: inline !important;
}

.dialog .inner
{
    width: 500px !important;
}

#rightcontainer .inner
{
    width: 320px !important;
}

.customObject.framed
{
    height: 50px !important;
}

.customObject.framed a
{
    margin-top: 0px !important;
    padding-top: 15px !important;
    background-position: 10px 8px  !important;
    height: 35px !important;
}

#toggle
{
    cursor: pointer !important;
}

.grid-full, .gmgrid
{
    position: relative !important;
}

.post-body .related
{
    margin-bottom: 30px !important;
}

.permalink .permatag
{
    position: absolute !important;
    bottom: 0px !important;
}

.post-body
{
    margin-bottom: 30px !important;
}

#rightwrapper
{
    right: 11px !important;
}

#rightbar_scroller
{
    overflow-y: auto !important;
    overflow-x: hidden !important;
}

.post-body [style^="background-color: #e2e0d4"], blockquote
{
    display: inline-block !important;
}

#top_search_control
{
    display: block !important;
}
}
