css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://dragcave.net") {

.csort2 img{
background: #3b1634 !important;
}

td, tr, th img{
background: #3b1634 !important;
}

table, td, td{
text-align: center !important;
font-weight: normal !important;
font-size: 13px !important;
border-spacing: 2px !important;
border-color: #d584c5 !important;
}


INPUT {
color:#444444 !important;
font:8pt arial !important;
}

TEXTAREA {
color:#444444 !important;
font:8pt arial !important;
}

html,body {
	padding: 0 !important;
	margin: 0 !important;
}
body {
	color: #f4bce9 !important;
	background: #000000 url("http://i206.photobucket.com/albums/bb147/sadakodragon/bgnebula.png") repeat-y fixed !important;
	font-size: 10pt !important;
	font-family: arial !important;
	color: #f4bce9 !important;
}
h1,h2 {
	padding: 0 !important;
	margin: 0 !important;
}

b {     
        color:#242072 !important;font:11px arial !important;
        font-style:italic !important;
        font-weight:bold !important;letter-spacing:-.5pt !important;
}

p {
	text-indent: 1.5em !important;
}
a:link, a:active, a:visited {
	color:#f4bce9 !important;
        font:13px arial !important;
        font-style:italic !important;
        font-weight:bold !important;
}
a:hover {
color:#f4bce9 !important;
        background:#242072 !important;
}
th,td {
	text-align: center !important;
}
table.vamid td {
	vertical-align: middle !important;
}
table.leftalign,table.leftalign td,table.leftalign th {
	text-align: left !important;
}
img {
	border-width: 0 !important;
}
acronym {
	cursor: help !important;
}
#wrapper {
	width: 700px !important;
	max-width: 600px !important;
	margin: auto !important;
	text-align: justify !important;
}
#top {
	background: url("http://i206.photobucket.com/albums/bb147/sadakodragon/dcnebulahead.png") no-repeat !important;
	width: 650px !important;
	color: #f4bce9 !important;
	height: 140px !important;
}
#middle {
	background: none repeat-y !important;
	padding: 0 40px !important;
	color: #f4bce9 !important;
	min-height: 15px !important;
	width: 570px !important;

}
#bottom {
	background: url("http://i206.photobucket.com/albums/bb147/sadakodragon/dcnebfoot.png") no-repeat !important;
	color: #f4bce9 !important;
	width: 750px !important;
	height: 40px !important;
}
#logo {
	display: block !important;
	margin: 0 auto !important;
	width: 562px !important;
	height: 127px !important;
	display:none !important;

}
#menu {
	text-align: center !important;
	margin: 0 auto !important;
}
.cave,.scroll,.login,.register,.shop,.forum,.logout {
	margin: 0 5px !important;
	background: transparent url("http://i206.photobucket.com/albums/bb147/sadakodragon/dcnebtopbar.png") no-repeat !important;
	height: 50px !important;


}
.cave {
	width: 85px !important;
    background-position: -0px center !important;
}
.scroll {
	width: 100px !important;
	background-position: -80px center !important;
}
.login {
	width: 86px !important;
	background-position: -192px center !important;
}
.register {
	width: 120px !important;
	background-position: -278px center !important;
}
.shop {
	width: 82px !important;
	background-position: -362px center !important;
	display:none !important;
}
.forum {
	width: 104px !important;
	background-position: -400px center !important;
}
.logout {
	width: 115px !important;
	background-position: -500px center !important;
}
#prefooter {
	margin-left: -11.5px !important;
	height: 90px !important;
	overflow: hidden !important;
	width: 728px !important;
	margin-right: -20px;
	display: none !important;
}
#footer {
	font-size: 0.8em !important;
	text-align: center !important;
	margin: 0 auto;
}
}
