css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("vkontakte.ru"),domain("vk.com") {

/* баннеры */
#banner1, #banner12, #ad_box_ad_0, #ad_box_ad_1 {
  display: none !important;
}

/* новые сообщения */
table#messages_rows tr.newRow {
 background-color: #d1e1ef !important;
}
table#messages_rows tr.newRow td.messageSnippet a.messageBody {
 color: #1d3d5f !important;
font-weight: bold !important;
}

/* группы с большими картинкаи*/
div.grouprow td.tunaimage a {
   display: block;
   height: 100px;
   overflow: hidden;
}

/* список групп */
div#profile_groups {
   font-size: 0px;
}
div#profile_groups a {
   display: block;
   padding: 5px 5px 5px 0px;
   font-size: 11px;
}

/* стрелки перехода по страницам */
.pagePhoto a {
   width: 40px;
   text-align: center;
   font-size: 15px !important;
}

div.app_subimage {margin:auto;}

/*td.info div {width: 500px !important;}*/
div[style*="width: 338px;"] {width: 100% !important;}
table.reply_table {width: 100% !important;}
div#career {width: 100% !important;}
div.button_yes {width: 200px !important;}
div.audioRow {width: 100% !important;}
td.tunaimage a {text-align:center;}

#pageHeader,#pageHeader1 {background-image: url("http://vk.com/images/xhead_fluid.gif")!important; background-position: left top !important;}
#pageHeader1 {background-image: url("http://vk.com/images/xheadblank_fluid.gif")!important;}
#pageHeaderRight {background: url("http://vk.com/images/xhead_fluid.gif") right bottom no-repeat !important; height: 45px !important;}

/* Горизонтальная растяжка с логотипом */
#pageHeader {
 position: fixed !important;
 z-index: 101;
 opacity: 0.9 !important;
}
#pageHeaderRight {
  background: none !important;
}
#wrapH {
position: fixed !important;
top: 45px;
z-index: 101;
opacity: 0.9;
width: 630px;
}
#wrap2 {
margin-top: 65px !important;
}

#sideBar {
position: fixed !important;
top: 45px;
z-index: 101;
opacity: 0.9;
}

/* Баннеры */
.ad_box,.ad_title,.ad_desc{display: none !important;}

/* Моя Страница */
.hasedit, 
a[href="/friends.php"], /* Мои Друзья */
a[href="/photos.php"], /* Мои Фотографии */
a[href="/video.php"], /* Мои Видеозаписи */
a[href="/audio.php"], /* Мои Аудиозаписи */
a[href^="/mail.php"], /* Мои Сообщения */
a[href="/notes.php"], /* Мои Заметки */
a[href="/groups.php"], /* Мои группы */
a[href="/events.php"], /* Мои Встречи */
a[href="/newsfeed.php"], /* Мои Новости */
a[href="/fave.php"], /* Мои Закладки */
a[href^="/gifts.php"], /* Мои Желания */
a[href="/settings.php"] /* Мои настройки */
{ margin-left: -2.1em; /* Передвигаю влево на расстояние слова "Мои" */ }

#nav /* Всё меню */
{ overflow: hidden; /* Прячу слово "Мои" */ }

#nav li /* Каждый элемент меню */
{ margin-left: -0.3em; /* Влево все, чтобы ровно было */ }
#nav li#myprofile a /*  кнопке "Страница" */
{ /*width: 106px !important;*/ /* Возвращаю ширину. */ /* Не получается */ }
.popup_box_container /* Всплывающее окно */
{ /*position: fixed;*/ /* Фиксирую на месте */ }

/* Группы */
#edu, #career {width: 50%; float: left}
#opinions, #places, #wall, #audios {clear: both}
#groups .flexBox {line-height: 1 !important}
#groups .flexBox a {display: block; position: relative; left: .75em; top: 1.25em}
#groups .aPad {max-height: 50em !important; overflow: hidden}
.profileName {width: auto !important}
.profileName h2 {font-size: 18px !important}
.profileName h2:after {color: silver; content: ' в контакте'}


}
