@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rotoworld.com") {

/*Move Player Updates to the Left */
.left_wide {float:right !important;}

/*Increase Player Update Font */
.pb .headline {font-size:14px !important;}
.pb .report, .pp .report, .pp .info{font-size:14px !important;}
.pb .impact, .pp .impact{font-size:14px !important;}

/*Remove Ads */
#RW_ad728{
width:0px !important; 
padding:0px 0px 3px 0px !important; 
border:none 0px !important; 
height:0px !important; 
}
#RW_ad728 object, #RW_ad728 img, #RW_ad728 iframe, #RW_ad728 div{ 
width:0px !important; 
height:0px !important; 
margin:0px 0px 0px 0px !important;
}
.RW_ad300{
width:0px !important; 
height:0px !important; 
margin:0px 0px 0px 0px !important;
}
.RW_ad300 .ad-container object{
width:0px !important; 
height:0px !important;
margin:0px !important;
}
#ad160x600{
width:0px !important;
margin-top:0px !important; 
padding:0px 0px 0px 0px !important;
}
#ad160x600 .adcontent{
height:0px !important;
width:0px !important; 
margin-top:0px !important;
}
embed{
height:0px !important;
width:0px !important; 
margin-top:0px !important;
}
}
