css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gizmodo.com"), domain("lifehacker.com"), domain("kotaku.com"), domain("gawker.com"), domain("deadspin.com"), domain("jezebel.com"), domain("io9.com"), domain("jalopnik.com")
{

body, .modal_overlay
{
    background: #07050f !important;
}

#pane, #top_search_control, #rightbar_scroller
{
    background: #111111 !important;
}

.inner, .ctedit
{
    background: #000000 !important;
}

.replies, .replies .comment, .code, .gallery-preview, #rightcontainer .post:hover, .searchOptions, #rightcontainer .post.current, .post-body [style^="background-color: #e2e0d4"], .actions, #userNotificationContainer, .replies .ctedit
{
    background: #191919 !important;
}

#rightcontainer
{
    background: #454545 !important;
}

#postform_widget form input[type="text"], #postform_widget form input.cn_image_url, #postform_widget form input.cn_video_url, #postform_widget form textarea
{
    background: black !important;
    color: #dddddd !important;
    border-color: #454545 !important;
}

.chrome
{
    background: -moz-linear-gradient(#222222, #0a0a0a) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#222222), to(#0a0a0a)) !important;
    border: #454545 solid !important;
    border-width: 1px 0 1px 0 !important;
}

.gbut .gbin .gbtx, .customObject.framed
{
    background: -moz-linear-gradient(#222222, #0a0a0a) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#222222), to(#0a0a0a)) !important;
    border: 1px #454545 solid !important;
}

button, .button
{
    background: -moz-linear-gradient(#222222, #0a0a0a) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#222222), to(#0a0a0a)) !important;
    border-color: #454545 !important;
    color: white !important;
    box-shadow: 0 0 0 0 !important;
}

button:hover, .button:hover, .dialog .inner h1
{
    background: #222222 !important;
}

.chrome a.chromebut:hover, .chrome a.chromebut.selected, blockquote, .form_part, .customObject.framed a:hover
{
    background-color: #222222 !important;
}

.gbut .gbin .gbtx:hover
{
    background: #222222 !important;
}

.chrome a.chromebut.selected
{
    text-shadow: 0px 0px !important;
}

.chrome a.chromebut.withlabel span
{
    background: -moz-linear-gradient(#222222, #0a0a0a) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#222222), to(#0a0a0a)) !important;
    border-right: 1px #454545 solid !important;
    -moz-border-radius: 0px 20px 20px 0px !important;
    -webkit-border-top-right: 20px !important;
    -webkit-border-bottom-right: 20px !important;
}

.inner
{
    border: 0px !important;
}

#date-nav
{
    border: 0px !important;
}

#rightcontainer
{
    border-color: #454545 !important;
    box-shadow: 0 0 0 !important;
}

#top_presence_control a, .chrome a.chromebut, .threed, blockquote, .form_part, #rightcontainer .post, .searchOptions, #userMenu, #date-nav
{
    border-color: #454545 !important;
}

#top_presence_control
{
    border: 0px !important;
}

.thread
{
    border-color: #454545 !important;
}

#date-nav
{
    border: 1px #454545 solid !important;
}

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, #postform_widget ul.tabs li, #postform_widget label, #postform_widget form label
{
    color: white !important;
}

body, p, .permalink .post-body, .ctext, .chrome, .permalink .permatag .tag
{
    color: #dddddd !important;
}

.small-light, .ctext .metatext
{
    color: #888888 !important;
}

.home
{
    background-image: url(data:image/png;base64,) !important;
}

.popular
{
    background-image: url(data:image/png;base64,) !important;
}

.search
{
    background-image: url(data:image/png;base64,) !important;
}

.gbi-prev, .gbi-next
{
    background-image: url(data:image/png;base64,) !important;
}

.icon_reply
{
    background-image: url(data:image/png;base64,) !important;
}

img[src="http://betacache.gawkerassets.com/assets/base.v10/img/ui/icon-ff.png"]
{
    background-image: url(data:image/png;base64,) !important;
    width: 0px !important;
    padding-left: 12px !important;
    height: 11px !important;
}

img[src="http://betacache.gawkerassets.com/assets/base.v10/img/ui/icon-rw.png"]
{
    background-image: url(data:image/png;base64,) !important;
    width: 0px !important;
    padding-left: 12px !important;
    height: 11px !important;
}
}

@-moz-document domain("lifehacker.com")
{
a
{
    color: #a3bc66 !important;
}
}

@-moz-document domain("gizmodo.com"), domain("deadspin.com"), domain("jalopnik.com")
{
a
{
    color: #78dfff !important;
}
}

@-moz-document domain("io9.com")
{
a
{
    color: #f1b7d8 !important;
}
}

@-moz-document domain("jezebel.com"), domain("kotaku.com")
{
a
{
    color: #D75148 !important;
}
}
