css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org")

{


#left-sidebar > NAV {background: #125bdb !important; -moz-border-radius: 10px !important;}

HEADER {background: #fe370e !important; -moz-border-radius: 10px !important;}

#related {background: #fe370e !important; -moz-border-radius: 10px !important;}

#Head {background: #fe370e !important; }

#Content { background: #1f402b !important; border: solid 10px #1f402b !important; -moz-border-radius: 10px !important;}

 .Tabs.DiscussionsTabs, .Tabs.ActivityTabs, .Tabs.ConversationsTabs, .Tabs.ProfileTabs, .Tabs.HeadingTabs.DiscussionTabs, .Tabs.HeadingTabs.ConversationTabs { background: #125bdb !important; -moz-border-radius: 10px !important; border-bottom: none !important}

A[href="/discussions"], .MyDiscussions, A[href="/activity"], A[href="/messages/all"], A[href="/profile/activity"], A[href="/profile/notifications"], #Content > DIV > DIV:first-child + DIV > UL > LI:first-child > A, A[href="/categories/stylish"],.WriteButton, .PreviewButton, .DraftButton, #Content > DIV > DIV:first-child + DIV > UL > LI:last-child > A, A[href="/categories/style-reviews"]
 {background: #193020 !important; -moz-border-radius: 10px !important; border: none !important}

.Category {background: #0b3489 !important}

.Box, ul.PanelInfo li {background: #7c6b2c !important;  -moz-border-radius: 10px !important; border: none !important}

#Head h1 a span {
   font-size: 24px;
   color:#eb3e1e !important;
   text-shadow: none !important;}

div.Tabs li a span {background: #7c6b2c !important}

#Panel > DIV > H4 {color: #da5f14 !important}

ul.PanelInfo {border-top: solid 1px #fe380f !important}

#Head form input.Button {color: #02132c !important; text-shadow: none !important; -moz-border-radius: 0px 7px 7px 0px !important;}

#Head > DIV > DIV > FORM { -moz-border-radius: 10px !important;}

.SubTab {background: #2d588d!important; -moz-border-radius: 6px !important}

ul.DataList div.Meta strong {background: #ff1f01 !important}

a.BigButton {background: #761c09 !important; border: none !important; text-shadow: none !important}

.PanelInfo {background: #691f07 !important; -moz-border-radius: 10px !important; border-bottom: none !important}

.MessageForm.CommentForm, #MessageForm {background: #2f76b0 !important; -moz-border-radius: 10px !important; border-bottom: none !important}

.Mine {background: #9b220b !important; -moz-border-radius: 10px !important; border-bottom: none !important}





html, body { background: none !important; color: #134647 !important; background-color: #93301a !important; }

#style-list { background-color: #134647 !important; border: solid 6px #293833 !important; -moz-border-radius: 10px !important }

a:link { color: #ccc  !important }

h1  { color: #ccc !important; background-color: #ce1616 !important; border: solid 10px #ce1616 !important; -moz-border-radius: 10px !important }

h2 { color: #ccc !important; background-color: #ce1616 !important; border: solid 6px #ce1616 !important;  -moz-border-radius: 10px !important }

#install { background-color: #167b1c !important; border: solid 0px #167b1c !important; -moz-border-radius: 10px !important}

a:hover, a:active , a:visited:hover { color:  #2e559a  !important; background: transparent !important }

a:visited { color: #ccc !important }

#search-terms, ul#navigation a, input[type="submit"] { background-color: #2e559a !important; border-color: dimgray !important }

input#search-terms { background-color: black !important }

pre { background-color: #167b1c !important; border: 1px solid darkgray !important }

#header-bar { background-color: #167b1c !important }

#Header { background-color: #ce1616 !important; background-image: none !important; border: none !important }

.NoNewComments, #Discussions li.Read ul {background-color: #2e559a !important}

#Discussions li ul {background-color: #167b1c !important}

.UnblockedCategory {background-color: #3e5c8e !important}

#Form form {background-color: #80240d !important; border: solid 1px #3d6f3b !important}

#Panel  {background-color: #5b954f !important; background-image: none !important; border: solid 4px #5b954f !important; -moz-border-radius: 10px 10px 10px 10px !important}

#AboutVanilla {background-color: #5b954f !important; -moz-border-radius: 10px !important}

BLOCKQUOTE, CITE  {background-color: #94230a !important; color: #e0eaf3 !important; border: solid 4px #e0eaf3 !important;   -moz-border-radius: 10px !important}

LABEL, #CommentFormats {color: #546f78 !important;}

LI {color: #ccc !important;}

#reviews  {background-color: #488025 !important; background-image: none !important; border: solid 4px #488025 !important; -moz-border-radius: 10px 10px 10px 10px !important}

#Header ul li a, .TabOn {background-color: #125bdb !important;  background-image: none !important;border: none !important;   -moz-border-radius: 6px 6px 0px 0px !important}

#Header ul li.TabOn a, #Header ul li.TabOn a:hover {background-color: #fe370e !important;}

#Session {color: #1f402b !important;}

#obsoletion-message {background-color: #264a2b !important; color: #1f402b !important; border: solid 1px #264a2b !important;   -moz-border-radius: 10px !important}

.CommentBody {background-color: #609634 !important; color: #1f402b !important; border: solid 6px #125bdb !important;   -moz-border-radius: 10px !important}

#discussions {background-color: #f64c11 !important; color: #ad010a !important; border: solid 6px #f64c11 !important;   -moz-border-radius: 10px !important}

#search-options {background-color: #f64c11 !important; color: #ccc !important; border: solid 6px #f64c11 !important;   -moz-border-radius: 10px !important}















/* full star (3) */
#discussions .good-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* empty star (1) */
#discussions .bad-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* half star (2) */
#discussions .ok-rating {
  list-style-image:  url("data:image/png;base64,")
    !important;
} /* */

/* grey diamond (0) */
#discussions .no-rating {
  list-style-image:  url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAOCAYAAADwikbvAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAGXRFWHRDb21tZW50AENyZWF0ZWQgd2l0aCBHSU1QV4EOFwAAACZJREFUeJxjYBgF1APl5eX3ydYIxO9JNgBJ43dKDSDf6WRpHMYAACQOHc2U8/yZAAAAAElFTkSuQmCC")
    !important;
} /* */




/*

  style average-rating

*/

#average-rating img.good-average-rating /*[src$="/good.gif"]*/ {

  width:  0  !important;

  padding:  0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important; 

}

#average-rating img.bad-average-rating /*[src$="/bad.gif"]*/ {

  width:  0  !important;

  padding: 0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important; 

}

#average-rating img.ok-average-rating /*[src$="/ok.png"]*/ {

  width:  0  !important;

  padding:  0 0 16px 16px  !important;

  margin:  0 0 0 4px  !important;

  background-image: url("data:image/png;base64,") 

    !important;

  background-repeat:  no-repeat  !important;

  background-position:  1px  1px  !important; 

}





}
