css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tamurayukari.com") {

*:not(input){
	font-family:"メイリオ","Meiryo","ヒラギノ丸ゴ Pro W4","Hiragino Maru Gothic Pro",Osaka,sans-serif !important;
}
#archive-box select {
	height:22px !important;
	margin-bottom:18px!important
}
select
,option{
	font-size:12px !important;
}
select{
	text-align:center !important;
}
option{
	text-align:left!important;
}



#comment-box{
	width:130% !important;
	height:80em !important;
	overflow:auto!important;

	border-top:30px solid #FFFFFF!important;
	border-bottom:30px solid #FFFFFF!important;
	border-left:20px solid #FFFFFF!important;
	border-right:20px solid #FFFFFF!important;
	margin-bottom:20px!important;

	position:relative!important;
	top:10px!important;
	left:-80px!important;

	-moz-border-radius:20px 20px 20px 20px !important;
	-webkit-border-radius:20px 20px 20px 20px  !important;
	-o-border-radius:20px 20px 20px 20px !important;

	-moz-box-shadow: 0px 0px 15px rgba(142, 185, 207,0.6);
	-webkit-box-shadow: 0px 0px 15px rgba(142, 185, 207,0.6);
	-o-box-shadow:0px 0px 15px rgba(142, 185, 207,0.6);

}
/*
.entry-pagecount:after{
	content:"comment" !important;

	position:relative!important;
	top:35px!important;
	left:170px!important;
}
*/
p.comment-title{
}
.user-comment{
	font-size:13px !important;
	line-height:1.4 !important;
}
#comment-box
,.text-box{
	z-index:1 !important;
}

/*フォーム*/
div#comment-box div#comment-form-box form#comments-form dl dt:last-of-type{
	color:#F1FAFF!important;
}
div#comment-box div#comment-form-box form#comments-form dl dt:last-of-type:after{
	content:"コメント" !important;
	color:#317090 !important;


	position:relative!important;
	top:0px!important;
	left:140px!important;
}
.comennt-caution{
	font-size:11px !important;
}
#commentName
,#commentMail
,#commentUrl{
	color:#5F5F5F!important;
}
textarea{
	width:100% !important;

	color:#5F5F5F!important;
	font-size:13px !important;
	line-height:1.4 !important;
}
.comment-btn{
	padding-bottom:10px!important;
}
}
