css:
@-moz-document domain('slashdot.org') {

#slashboxes article header h2, #firehose article header h2, #comments h4, #modal_box #editor h2.firehose, .generaltitle h3 {
font-family:arial,serif;
font-weight:bold;
padding:4px 10px;
}

#comments .details {
padding:4px 10px;
}

#commentlisting .comment_footer, #commentlisting.d1 .commentBody, #comments .commentBody, #wide #commentlisting .commentBody, #comments ul li.show {
padding:10px 10px 0;
}

#commentlisting.d1 .commentSub, #wide #commentlisting .commentSub, #comments .commentSub {
padding:0 10px;
}

#comments ul ul, #comments ul li ul li {
margin: 0 5px 10px !important;
}

#comments .commentSub {
height:1.5em;
}

#comments .oneline {
text-indent:10px;
height:30px;
}

p {
margin:0 0 .5em;
}

#comments .oneline h4, #comments li .oneline .commentBody {
line-height:30px;
}
#comments .oneline .commentBody {
line-height:30px;
}

#commentlisting li:not(.comment) {
margin-bottom:0;
}

.commentSub .btn {
padding-top:0;
}


#firehose article header .details, #comments article header .details, #comments .full .detail, #modal_box #editor #editpreview .details {
padding: 6px 10px;
}

.article .body .p {
margin: 10px;
}

#firehose article footer {
margin: 0 10px 10px;
}

#firehose article:not(.briefarticle) {
margin-bottom: 0px;
}

#firehose article header .topic, #editpreview article header .topic {
top: 10px;
right: 10px;
}

#firehose, #firehose-mq {
margin-right: 0px;
}

#slashboxes {
display:none;
}

}
