css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("resource://brief-content/feedview.html") {

/* Compact Brief v1.5 by Ngamer01.  Compact Brief was developed from the original style sheet by ancestor for Brief 1.5 to 1.5.4. */

/* ----------------------  Headlines view   ---------------------- */

.article-container[collapsed] {
    min-height: 32px !important;
}

.article-container[collapsed] .article-date {
    display: none !important;
}

.article-container[collapsed]:hover .article-date {
    display: none !important;
}

.article-container[collapsed] .feed-name {
    width: 0% !important;
    height: 0% !important; /* Height of a single line */
    overflow: visible !important;
}}
