css:
@-moz-document domain(forums.white-wolf.com)
{
	/* Changing colours to white/grey/blue on black, increasing font size*/

	.yafnet .innerquote {
	  background-color: #444 !important;
	  color: #FFF !important;
	}

	.yafnet, .yafnet td {
	  color: #FFF !important;
	  background-color: #222 !important;
          font-size: 12px !important; /* 12p font is much more pleasant to read*/
	}

	.yafnet .postheader td {
	  color: #BBB !important;
	  background-color: #333 !important;
	}

	html, body {
	  background-color: #000 !important;
	}
	
	/*uglies hack in this whole CSS hack, modifies all spans that use blue to be "link" blue, no longer with Demetrius7997 be hard to read on the forums. */
	span[ style="color:blue"]{
		color: #0F86A8 !important;
	}

        /*Some users on linux experienced problems because somehow the span code is slightly different, this should fix it */
        span[ style="color: blue;"]{
		color: #0F86A8 !important;
	}
	
	body#forum #container {
		background-color: #222 !important;
	}

	#login{
		color: #BBB !important;
	}
	
	/* Minor modifications to fix readability issues cropped up colour changes above */
	
	/* bbcode buttons are transparent, this fixes readablity of them */
	#bbcodeFeatures tbody tr td{
		background-color: #444!important;
	}
	
	/* editbox colour */
	.yafnet textarea.BBCodeEditor {
		color: #FFF !important;
		background-color: #444!important;
	}
	
	.yafnet textarea.basicBBCodeEditor{
		color: #FFF !important;
		background-color: #444!important;
	}
		
	/* dropdown menu backgrounds */
	.yafnet .yafpopupmenu li {
		background-color: #444 !important;
	}
	
	/* pm colours */	
	.ui-widget-content a {
		color: #0F86A8 !important;
	}
	
	.yafnet a.yafcssbigbutton span {
		color: #FFF !important;
	}
	
	/* my topics page */
	.yafnet table.content {
		background-color: #222 !important;
	}
	
	/*Changing width to use whole page*/
	#container {
		width: 95% !important;
	}
	
	#navigation {
		width: 100% !important;
		background-size: 100% !important; 
	}
	
	footer {
		width: 100% !important;
		background-size: 99% !important; 
	}
	
		
	#search{
		float: right !important;
		left: 0px !important;
	}
	
	td.postUser {
		width: 200px !important;
	}	

	td.postPosted  {
		width: 90% !important;		
	}
	
	td.header2{
		text-align: left !important;
	}
	
	/* Pink Selections bad, fixing */
	::-moz-selection{ 
		background: #0000CD	 !important;
		
	} 	
	
	::selection{ 
		background: #0000CD	 !important;
		

	} 	
 
}
