/* ================================================================================================================================ START */

body {
    background-color:#ccd9c8;
    font-family:Arial, sans-serif;
    font-size:12px;
    /*text-shadow: 0 1px rgba(255,255,255,0.4);*/
    padding-top:0;
    padding:0 1em;
    margin:0px;
    }
    .body {
	background: #ccd9c8;
	border: 0 none !important;
	color: #2C3635;
        line-height:1.5em;
        margin-bottom:1em;
        margin-top:0em;
        overflow:hidden;
        padding:.5em;
        white-space:normal;
        word-wrap:break-word;
    	-moz-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
        box-shadow:0px 1px 3px rgba(0,0,0,0.35);
        -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    }
    .body a {
        color: #2C3635;
    }
    .body img {
        box-shadow: rgb(142, 148, 141) 0px 1px 3px;
        -moz-box-shadow: rgb(142, 148, 141) 0px 1px 3px;
        -webkit-box-shadow: rgb(142, 148, 141) 0px 1px 3px;
        border:none!important;
        float:left;
        margin-bottom:6px;
        margin-right:7px;
    }
    .body ul.menu {
        clear:both;
        margin:.5em 0 0;
        text-align:right;
    }
        .body ul.menu li {
            margin:0 0 0 .5em;
        }

.codebox {
    overflow: auto;
    display: block;
    height: auto;
    max-height: 250px;
    white-space: normal;
    padding-top: 5px;
    font: 0.9em monaco, "andale mono","courier new", courier, mono;
    line-height: 1.3em;
    color: #393939;
    margin: 3px 0;
}

span.quote {
    color:#8454ff;
}

.highlighted {
    background-color: #b9c9bb;
}

.spoiler {
    background-color:#000;
    color:#000 !important;
    cursor:default;
}
    .spoiler:hover {
        color:#fff !important;
    }

.border {
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border: 1px solid #B5BFB5;
    background: #DDE5D8;
    border-top-left-radius: 6px 6px;
    border-top-right-radius: 6px 6px;
    border-bottom-right-radius: 6px 6px;
    border-bottom-left-radius: 6px 6px;
    margin:1em;
    padding:.5em;
}

.highlight {
    background-color:#ffff99;
    font-style:normal;
    font-weight:700;
}

a {
    color:#000;
}
    a:focus {
        outline-style: none;
    }

#notice {
    background: #dbe4da url(http://st.deviantart.net/minish/gruzecontrol/cruiser.gif) repeat-x;
    padding:.5em;
    margin-top:1em;
    margin-left:-1em;
    margin-right:-1em;
    margin-top:0!important;
}

h1 {
    font-size:17px;
    text-align:center;
    display:none;
}
    h1 a {
        color:#000;
        text-decoration:none;
    }

h2 {
    font: bold 16pt Trebuchet MS,sans-serif;
    letter-spacing: -1px;
    color: #414D4C;
    margin-top:0.2em !important;
    padding-bottom:-0.5em !important;
}
    h2 a:hover {
    text-decoration:underline;
    }

h3 {
	background: #ccd9c8 url(http://st.deviantart.net/minish/browse/bthead.png) repeat-x;
    border-bottom: 1px solid #BAC7BB;
    font-size:12px !important;
    color: #2C3635;
    /* text-shadow: 0 1px 0 rgba(255, 255, 255, 0.6); */
    cursor:default;
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    font-size:12px;
    font-weight:400;
    margin:0;
    padding:.5em;
    overflow:auto;
}
    h3 a {
	color: #2C3635;
    }

.reply_id {
    float:right;
    text-align:right;
    vertical-align:middle;
}

h4.section {
    font-size:15px;
    margin-bottom:7px;
}

h4.user {
    font-size:14px;
    margin:0 0 .5em;
}

label.common {
    float:left;
    padding-right:.5em;
    text-align:right;
    width:15em;
    font-weight:normal;
}

input,textarea {
    display:block;
}    
    input,textarea:focus {
        outline:none;
    }

textarea {
    font-family:monospace;
    width:100%;
}

fieldset {
    margin:1em 0;
}

ul.body,ul.error_message {
    list-style-type:none;
}

ul.error_message {
    margin:0;
    padding:0;
}
    ul.error_message li {
        font-style:italic;
        padding:.2em;
    }

ul.menu {
    margin:1em;
    text-align:center;
}
    ul.menu li {
        display:inline;
        font-weight:700;
        margin:0 1em;
    }

ul#main_menu {
    clear:both;
    background: #46574C url(http://st.deviantart.net/minish/main/v7footer/gradient.png) repeat-x top left;
    /* background: #46574C url(http://st.deviantart.net/minish/main/bggr.png) repeat-x top left; */
    padding-top:0.7em;
    padding-bottom:0.6em;
    margin-left:-1em !important;
    margin-right:-1em !important;
    margin-top:0!important;
    /* background-color: #637768; */
}
    ul#main_menu a {
	    font-weight:bolder;
	    font-size:14px;
	    color:#fff;
	    text-shadow: #111 1px 1px 1px;
	    text-decoration:none;
    }
    ul#main_menu a:hover {
	    font-weight:bolder;
	    color: #fff;
	    text-shadow: #000 1px 1px 1px;
	    text-decoration:none;
    }

ul.stuff {
    margin-bottom:2em;
}
    ul.stuff li {
        margin-bottom:.5em;
    }

table {
    background: #dbe4da url(http://st.deviantart.net/minish/gruzecontrol/cruiser.gif) repeat-x;
    -webkit-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    -moz-box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    box-shadow:0px 1px 3px rgba(0,0,0,0.35);
    border-collapse:collapse;
    margin-bottom:1em;
    color: #3B5A4A;
    margin-left:0em;
    margin-right:0em;
    width:100%;
}
    table a {
	text-decoration:none;
	font-size:12px !important;
	font-weight:normal !important;
	color: #2C3635;
    }
    table a:hover {
	text-decoration:none;
	color: #3B5A4A;
    }
    table th {
	background: #dbe4da url(http://st.deviantart.net/minish/gruzecontrol/cruiser.gif) repeat-x;
        border-bottom: 1px solid #BAC7BB;
        font-size:12px !important;
        color: #2C3635;
        text-shadow: 0 1px 0 rgba(255, 255, 255, 0.6);
        cursor:default;
        padding:.5em;
        text-align:left;
    }
    table td {
    	background-color:#d6ded4;
        padding:.5em;
        vertical-align:top;
    }
    table tr.odd td {
    	background-color:#ccd7cc;
    }
    table tr:hover td {
        background-color:!important;
    }
    table .minimal {
        white-space:nowrap;
        font-size:11px;
        width:1%;
    }
    table .minimal a {
        font-size:11px !important;
    }

.profile_fix {
    overflow:hidden;
    white-space:normal;
    word-wrap: break-word;
}

.reply_body_snippet,.snippet {
    font-style:italic;
}

.topic_headline a:visited {
    font-weight:400;
}

.topic_info {
    float: right;
    cursor: default;
}

em.marked {
    background-color:#ff9;
    font-style:normal;
    font-weight:700;
}

p.caption {
    font-size:11px;
    margin-top:2px;
}

.inline {
    display:inline;
}

#last_seen_marker {
    border-top: 1px dotted #9EB1A2;
}

#snapback_link {
    left:2px;
    position:fixed;
    top:50%;
}

.video,video,a.video {
    margin-top:0em;
    width:500px;
    margin-left:auto;
    margin-right:auto;
    margin-bottom:1em;
    color: red;
}

.standalone,.row {
    margin-bottom:1em;
}

.body ul.menu li,.body ul.menu li a,.body ul.menu li span,.unimportant,.unimportant * {
    font-size:10px;
}

a:hover,h2 a,#snapback_link a {
    text-decoration:none;
}

a.youtube_alternate,.noscreen {
    display:none;
}

h3#preview,h3#error,h3#replying_to,label,legend,.topic_headline a,.reply_body_snippet a,.snippet a {
    font-weight:700;
}

.help,.help_cursor {
    cursor:help;
}

input[type^="radio"] {margin-right:3px !important;}
input[id^="phrase"] {width:700px !important;}

div[style^="clear: both"] {display:none !important;}

#main_menu li.watchlist {display:none!important; }
#main_menu li.shuffle {display:none!important;}
#main_menu li.replies {display:none!important;} 
#main_menu li.hot_topics {display:none!important;}
#main_menu li.events {display:none!important;}
/* #main_menu li {float:right !important;} */

a[href*="report_topic"] {padding-right:3px !important;}

a[href*="report_reply"] {display:none !important;}

a[href*="new_reply"] {color: #2C3635 !important;}

:active, :focus {outline: none; -moz-outline-style: none;}
:focus {outline: none;} a:focus {outline-style: none;}

.quick_reply > input[name^="post"] {float:right; display:inline !important;}
.quick_reply > input[name^="preview"] {float:right; display:inline !important;}
input[name^="image"] {display:inline !important;float:right !important;}

.bar_container {
    border-color:#c1cdc2 !important;
}
.bar {
    background: transparent url(http://st.deviantart.net/minish/main/v7footer/poll_orange.gif) repeat-x center left;
    height:1em !important;
}

input::-webkit-input-placeholder, isindex::-webkit-input-placeholder, textarea::-webkit-input-placeholder {
    color: white;
}

input[id^="poll_option_"] {width:700px !important;}

/* ================================================================================================================================ END */
