@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

#urlbar,
.searchbar-textbox {
	font-size: 12.5px !important}
#identity-box {
	font-size: 12px !important}
#notification-popup-box {/* fix urlbar notification background */
	background: -moz-linear-gradient(left, rgba(255,255,255,0), White 97%) padding-box !important;
	padding-right: 0 !important;
	padding-left: 6px !important}

#main-window[sizemode=normal][tabsontop=true] #nav-bar:not(:-moz-lwtheme) {
	border: 1px solid RGBa(10%,10%,10%,.4) !important;
	border-bottom: none !important}
#PersonalToolbar {
	padding: 0 1px !important}
#main-window[tabsontop="true"] #PersonalToolbar {
	border-color: RGBa(10%,10%,10%,.4) !important}
#main-window[sizemode="maximized"][tabsontop="true"]:not([inFullscreen]) #TabsToolbar {
	margin-right: -14px !important;
	margin-left: -1px !important}
#main-window[tabsontop=false]:not(:-moz-lwtheme) #PersonalToolbar {
	background-color: RGBa(255,255,255,.15) !important;
	background-image: none !important;
	background-clip: padding-box !important;
	text-shadow: 0 0 10px white, 0 0 10px white, 0 1px 0 RGBa(255,255,255,.3) !important}
#main-window[tabsontop=false]:not(:-moz-lwtheme) #PersonalToolbar[collapsed="false"] + #TabsToolbar {
	background-color: RGBa(255,255,255,.15) !important;
	background-clip: padding-box !important}
#navigator-toolbox::after {
	background-color: RGBa(10%,10%,10%,.4) !important}
#main-window[sizemode=normal][disablechrome] #navigator-toolbox[tabsontop=true]:not(:-moz-lwtheme)::after {
	background-color: hsl(210,75%,92%) !important; /*Firefox 14*/
	background-color: #CCD9EA !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.5)) !important}

#browser-border-start:not(:-moz-lwtheme), #browser-border-end:not(:-moz-lwtheme) {
	display: none !important}
#main-window[sizemode=normal] #browser:not(:-moz-lwtheme) {
	-moz-appearance: none !important;
	border: 1px solid RGBa(10%,10%,10%,.4) !important;
	border-top: none !important;
	border-bottom: none !important}

#browser-bottombox:not(:-moz-lwtheme) {
	-moz-appearance: none !important;
	background-color: Transparent !important;
	background-image: -moz-linear-gradient(rgba(10%,10%,10%,.4) 1px, rgba(255,255,255,.15) 1px),
					  -moz-linear-gradient(rgba(255,255,255,0) 1px, rgba(255,255,255,.15) 1px, rgba(255,255,255,.15)) !important;
	background-size: 100% 2px, 100% 100% !important;
	background-repeat: no-repeat !important;
	background-clip: padding-box !important;
	border-color: rgba(10%,10%,10%,.4) !important;
	border-top: none !important}
#main-window[stylish-url="about:blank"] #browser-bottombox, #appcontent	{
	background : RGBa(255,255,255,.15) padding-box !important}
#main-window[stylish-url="about:blank"]:not([onclose^="PrintUtils"]):not([title^="View Source Chart"]) #content browser:not([src]) { 
	opacity: 0 !important}

#addon-bar:not(:-moz-lwtheme), #FindToolbar:not(:-moz-lwtheme) {
	-moz-appearance: none !important;
	background: none !important;
	border: none !important}
#FindToolbar:not(:-moz-lwtheme) {
	padding: 0 !important;
	color: Black !important;
	text-shadow: 0 0 10px White, 0 0 10px White , 0 1px 0 rgba(255,255,255,.3) !important}

#FindToolbar .findbar-textbox{
	-moz-appearance: none !important;
	background: rgba(255,255,255,.725) padding-box !important;
	border: 1px solid rgba(0,0,0,.32) !important;
	border-radius: 2.5px !important;
	padding: 1px !important}
#FindToolbar .findbar-textbox:not([focused]):hover{
	background-color: rgba(255,255,255,.898) !important}
#FindToolbar .findbar-textbox[focused="true"]{
	background-color: White !important}
#FindToolbar .findbar-textbox[status="notfound"] {
	background-color: #B22222 !important}
#FindToolbar .findbar-find-next, #FindToolbar .findbar-find-previous, #FindToolbar .findbar-highlight {
	color: Black !important}
#FindToolbar .findbar-find-next > label, #FindToolbar .findbar-find-previous > label {
	display: none !important}
#FindToolbar .findbar-find-next > image, #FindToolbar .findbar-find-previous > image {
	margin: 0 !important}
.findbar-find-next, .findbar-find-previous, .findbar-highlight {
	list-style-image: url("data:image/png;base64,") !important}
/* find-next button */
.findbar-find-next {
	-moz-image-region: rect(0 16px 16px 0) !important}
.findbar-find-next:hover {
	-moz-image-region: rect(16px 16px 32px 0) !important}
.findbar-find-next[disabled="true"] {
	-moz-image-region: rect(32px 16px 48px 0) !important}
.findbar-find-previous {
	-moz-image-region: rect(0 32px 16px 16px) !important}
.findbar-find-previous:hover {
	-moz-image-region: rect(16px 32px 32px 16px) !important}
.findbar-find-previous[disabled="true"] {
	-moz-image-region: rect(32px 32px 48px 16px) !important}
.findbar-highlight {
	-moz-image-region: rect(0 48px 16px 32px) !important}
.findbar-highlight:hover {
	-moz-image-region: rect(16px 48px 32px 32px) !important}
.findbar-highlight[disabled="true"] {
	-moz-image-region: rect(32px 48px 48px 32px) !important}
.findbar-highlight:active, .findbar-highlight[checked="true"] {
	-moz-image-region: rect(48px 48px 64px 32px) !important}
.findbar-highlight[checked="true"]:hover {
	-moz-image-region: rect(64px 48px 80px 32px) !important}
#sync-notifications, #content {
	box-shadow: none !important;
	background-color: Transparent !important;
	background-clip: padding-box !important}

notification {
	-moz-appearance: none !important;
	background-color: RGBa(255,255,127,.5) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-warning.png") !important;
	background-repeat: repeat-x !important;
	background-clip: padding-box !important;
	font-weight: bold !important;
	color: #916D15 !important;
	text-shadow: 0 0 .5em white, 0 0 .5em white, 0 1px 0 RGBa(255,255,255,.4) !important}
notification[type="critical"] {
	background-color: RGBa(255,63,63,.5) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-error.png") !important;
	color: #864441 !important}
notification[type="info"] {
	background-color: RGBa(112,162,232,.5) !important;
	background-image: url("chrome://mozapps/skin/extensions/stripes-info-negative.png") !important;
	color: #3F3F3F !important}
notification > * {
	background-clip: padding-box !important;
	border: none !important;
	border-top: 1px solid RGBa(10%,10%,10%,.4) !important;
	border-bottom: 1px solid RGBa(10%,10%,10%,.4) !important}
#content notification > * {
	border-top-color: Transparent !important;
	background-clip: padding-box !important}
#sync-notifications notification > * {
	background-clip: padding-box !important}

#browser-bottombox .toolbarbutton-menu-dropmarker .dropmarker-icon {
	display: none !important}
#browser-bottombox :-moz-any(#back-button, #forward-button, #reload-button, #stop-button, #home-button, #print-button, #downloads-button, #history-button, #bookmarks-button, #bookmarks-menu-button, #new-tab-button, #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button, #feed-button, #alltabs-button, #tabview-button):not(:-moz-any(#alltabs-button,#tabview-button,#new-tab-button,#sync-button[status])) > .toolbarbutton-icon {
	list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important}
