css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ling.pl") {

#pasektop, #footer, #arbodiv, .liniapozioma_gora, .liniapozioma_dol, #langcontainer, #sackdata, .infocolumnsblock,
#replaceme>div:first-child, .overdefinitiondiv, .dictsmallprint, #wordform>div:nth-child(2), #wordform>div:nth-child(3), 
body>div:nth-child(3),
body>div:nth-child(4),
#pasekwyszukiwacza>div:nth-child(3),
#pasekwyszukiwacza>div>div:first-child,
#pasekwyszukiwacza>div>div:nth-child(3)>div:first-child,
#pasekwyszukiwacza>div>div:nth-child(2)>div:first-child,
#pasekwyszukiwacza>div>div:nth-child(2)>div>div:first-child,
#klawiatura,
#pokazujPodpowiedzi,
#buttonsRange
 {
display: none !important;
}

body>div>div, #replaceme {
width: 100% !important;
margin: 0 !important;
overflow: hidden;
}
#definitionDIV {
width: 800px !important;
float: none !important;
clear: both !important;
margin: 25px auto !important;
padding: 0 25px !important;
background: #FFF !important;
overflow: hidden !important;
}
body {
color: #c9d3d6;
 background: #c9d3d6 !important;
}
#replaceme {
color: #000000;
}

.dictname {
text-decoration: none !important;
    display: block !important;
    margin: 12px 0 !important;
    border-bottom: 1px dotted #c9d3d6 !important;

    color: #779298 !important;
    letter-spacing: 0.15em !important;
    text-transform: lowercase !important;
    font-variant: small-caps !important;
font-weight: bold !important;
}

.dictword, .goleslowo {
margin: 0 !important;
padding: 0 !important;
line-height: auto  !important;
background-color: transparent !important;
font-size: 16px !important;
letter-spacing: 0  !important;
}

.dictword {
margin-bottom: 5px !important;
}

.dictdef {
margin-left: 20px !important;
}

#wordform {
background: #FFF !important;
width: 840px !important;
padding: 5px !important;
overflow: hidden !important;
margin: 0 auto !important;
}

#pasekwyszukiwacza {
margin-left: 0 !important;
border-top: none !important;
border-bottom: none !important;
margin: 0 auto !important;
height: auto !important;
width: 800px !important;
padding: 10px 20px !important;
background: #566e73 !important;
}

#pasekwyszukiwacza>div {
margin-left: 0 !important;
border-top: none !important;
border-bottom: none !important;
margin: 0 !important;
height: 30px !important;
width: 100% !important;
padding: 0 !important;
overflow: hidden !important;
}
#searchword {
height: 30px !important;
width: 700px !important;
border: none none none none !important;
padding: 0  15px !important;
margin: 0 !important;
font-size: 20px !important;
}
#pasekwyszukiwacza>div>div:nth-child(2){
width: 700px !important;
}
#pasekwyszukiwacza>div>div:nth-child(3){
width: 100px !important;
}
#pasekwyszukiwacza>div>div:nth-child(3) div {
width: 100px !important;
}
#pasekwyszukiwacza>div>div:nth-child(3) div img {
display: none !important;
}
#pasekwyszukiwacza>div>div:nth-child(3) input {
display: block !important;
width: 100px !important;
	background-image: -webkit-gradient(
		linear,
		left bottom,
		left top,
		color-stop(0.35, rgb(229,229,229)),
		color-stop(0.68, rgb(255,255,255))
	) !important;
	background-image: -moz-linear-gradient(
		center bottom,
		rgb(229,229,229) 35%,
		rgb(255,255,255) 68%
	) !important;
	border-radius: 7px !important;
	-moz-border-radius: 7px !important;
	-webkit-border-radius: 7px !important;
	border: solid 1px #bbbbbb !important;
	color: #444 !important;
	font-weight: bold !important;
	height: 28px !important;
	font-size: 14px !important;
	padding: 0 !important;
cursor: pointer !important;
}
}
