css:
/* iPad Series - Page flip
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") 

{

div#mainContainer {padding-left:10px;
				   width: 972px !important;
				   border-right:none !important;}      
div#pageHead.ptm.clearfix {padding-left:10px;}
#headNav {width: 790px;
		  border-right: none !important;}
#leftCol {margin-left: -9px;
	      padding-left: 10px;
	      width: 180px;}

	      
#blueBar       { background-image: url(http://www.meetingstreetmarket.com/1/BackN.png);
	background-repeat: repeat-x;
}


body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US { background-color: #eeeff4;}

#contentCol {background-image: url(http://www.meetingstreetmarket.com/1/PageN.png);
	background-repeat: repeat-y;
	width: 788px;
    border-left-style: solid;
	border-left-color: #29282a;
	border-color: #4f5155;
	left: -5px;
	margin-top: -2px;
}

div#sideNav {
	margin-right: 6px;
}

div#contentCol.clearfix.hasRightCol {
	margin-left: 183px;
}

body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US {background-image: url(http://www.meetingstreetmarket.com/1/BackN.png);
	background-repeat: repeat !important;}

#leftCol {
	background-image: url(http://www.meetingstreetmarket.com/1/ColumnN.png);
	width: 177px;
	margin-top: -20px;
	padding-bottom: 6px;
	padding-left: 6px !important;
	margin-left: -4px !important;
	position: fixed;
}

div#globalContainer {background-image: url(http://www.meetingstreetmarket.com/1/BackN.png);
	background-repeat: repeat !important;}
	

span.linkWrap {
	color: #abaeb4;
	font-size: 12px;
	text-shadow: #000 0px 1px 0px !important;
}

div#contentCol.clearfix.hasRightCol {text-shadow: #fff 0px 1px 0px !important;}

#headNav div.lfloat {
	margin-left: 80px;
	height: 30px;
	width: 358px;
	border: 2px outset #b6b6b6;
}

.profile .profile_color_bar  { background-color:#5d5d5f !important; }

/* navigation */

#headNav {background-image: url(http://www.meetingstreetmarket.com/1/SubNavNN.png);
	background-repeat: repeat-x;
	width: 766px;
	margin-left: 182px !important;
	height: 116px;
	padding-right: 8px;
	margin-top: -20px;
}

#pageLogo a    { background:url(http://www.meetingstreetmarket.com/1/FacebookBlue2.png) no-repeat scroll -21px -3px !important;
}

#headNavOut    { background:#8b8a8b !important; border-color:#515151 !important; }
#content       { background-image: url(http://www.meetingstreetmarket.com/1/Back4.png);
	background-repeat: repeat !important; }

/* main nav icons */ 
#jewelRequest { 
   background-image: url("http://www.meetingstreetmarket.com/1/FacebookBlue2.png") !important;
   background-position: -104px -107.5px !important; }
#jewelMail { 
  background-image: url("http://www.meetingstreetmarket.com/1/FacebookBlue2.png") !important;
  background-position: -104px -75px !important; }
#jewelNotif { 
  background-image: url("http://www.meetingstreetmarket.com/1/FacebookBlue2.png") !important;
  background-position: -104px -139px !important; }
#jewelCase .jewel  { border:none !important; }
.jewelToggler:hover { background-color: #5d5d5f !important; }


/* search */
#navSearch       { border-color:#ad6fa3 !important; }
#navSearch input { border-color:#000 !important; }
#navAccountLink:hover { color: #474a4f; }

/* Control Remarks Color */
#home_stream, .ufiItem, .mall_divider_text {background: rgba(151,193,235,.001) !important;
}

div.home_right_column {
	margin-right: 15px;
	margin-left: 8px;
}

div.ego_section {
	margin-right: 15px;
}

#event_create_mini_form div.mbs input.inputtext.event_name.DOMControl_placeholder {
	width: 215px;
}

div#pageHead.ptm.clearfix {
	width: 970px;background-image: url(http://www.meetingstreetmarket.com/1/Back4.png);
	background-repeat: repeat-x;
}


/* Messages adjustment */
div.UIWell {
	margin-right: 22px;}

div.groupProfileHeaderWash {
	width: 730px;
	margin-left: 2px;}

div.UIImageBlock.clearfix {
	padding-left: 5px;}

a.uiButton {
	margin-right: 30px;}

div#gb_content_and_toolbar {
	width: 735px;}
/* End Message Adjustment */

#footerContainer {background-image: url(http://www.meetingstreetmarket.com/1/Back4.png) !important;
	background-repeat: repeat !important;}

#contentCurve {background-image: url(http://www.meetingstreetmarket.com/1/Back4.png);
	background-repeat: repeat !important;}




/* Remove Borders & Lines */
* { border-color: #6a6f76 !important;}

/* nav tabs */
#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#343434 !important; color:#ecebeb !important; }


/* boxes */
.lightblue_box  { background-color:#5d5d5f !important; border-color:#5d5d5f !important; }
.commentable_item .ufi_section  { background-color: #5d5d5f; }
textarea, .inputtext, .inputpassword  { border-color:#abaaab!important; }


/* links */
a  { color: #a0a2a3;}

.uiLinkButton input  { color: #6c6d6d; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color: #606161; }
button.as_link  { color: #525353; }


/* side nav */
.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#797979 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color: #2e3136; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#2e3136 !important; }

.unread  { background-color:#cfcdce !important; }
.line  { color: #4c4d4d; }


/* pop up box  */
#jewelCase .jewelFooter a   { background-color:#363636 !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#363636 !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color: #555656; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#cfcece !important; border-color:#aeadad !important; }
#jewelCase .jewelBox  { background-color:#ecebeb !important; }


/* copy */
.uiHeader h2  { color: #494a4a; }


/* graffiti */
.current_stuff td  { background-color:#e8e6e7 !important; border-color:#c2c2c2 !important; }


/* profile page */
.UIComposer .UIComposer_Box  { background-color:#e4e2e3 !important; border-color:#cecece !important; }
.profile .box .box_header  { background-color:#e4e2e3 !important; border-color:#cecece !important; }


/* profile tabs */
.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ECECEC !important; border-color:#8f8f8f !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#b1b0b0 !important; border-color:#8f8f8f !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#403f3f !important; color:#ecebeb !important; border-color:#434243 !important; }


/* chat */
#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#5d5d5f !important;  }
#chat_tab_bar .chat_header  { background-color:#8d8d8d !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ecebeb !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#bebdbd !important; color:#383838!important; }
#presence_bar .presence_menu_opts h2 span  { background:#a4a3a3 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#a8a8a8 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#e1e1e1 !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#979696 !important; border-color:#777777 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ecebeb !important; }

#presence .presence_menu_opts  { border-color:#9d9c9c !important; }



/* buttons */
.UIButton_Blue  { background:#484848 !important; border-color:#9c9b9b !important; }
.UIComposer_InputArea  { border-color:#b3b2b2 !important; }
.uiButtonConfirm  { background:#515050 !important; border-color:#8e8e8e !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ecebeb !important; }


}
