{#blueBar       { background-image: url(http://www.meetingstreetmarket.com/1/Back1.png);
	background-repeat: repeat-x;
}

#navItem_nf a.item {
	margin-right: px;
	background-color: #666366;
}

body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US { background-image: url(http://www.meetingstreetmarket.com/1/Back1.png)}


#contentCol {background-image: url(http://www.meetingstreetmarket.com/1/Page6.png);
	background-repeat: repeat-y;
	width: 810px;
	margin-top: -3px;
	border-left-style: solid;
	border-left-color: #29282a;
	border-color: #4f5155;
}



div#leftCol {
	color: #f5f5f5;
}

span.linkWrap {
	color: #dde1e1;
	font-size: 11px;
