css:
/* iPad Series: Notepad
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") 

{
  
#blueBar       { background-image: url(http://www.meetingstreetmarket.com/1/MainNav1.png);
	background-repeat: repeat-x;
}

#navItem_nf a.item {
	margin-right: -1px;
	background-color: #666366;
}

body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US { background-image: url(http://www.meetingstreetmarket.com/1/Leather.png)}

#contentCol {background-image: url(http://www.meetingstreetmarket.com/1/PageL.png);
	background-repeat: repeat-y;
	width: 788px;
	margin-top: -1px;
}

div#contentCol.clearfix.hasRightCol {
	margin-left: 181px;
}

div#leftCol {
	color: #f5f5f5;
	margin-left: -4px;
}

span.linkWrap {
	color: #868888;
	font-size: 12px;
}

div.uiHeader.uiHeaderWithImage.uiHeaderPage.fbx_stream_header {
	width: 480px;
}

body.ego_page.home.hasLeftCol.fbx.safari4.Locale_en_US {background-image: url(http://www.meetingstreetmarket.com/1/Leather.png);
	background-repeat: repeat !important;}

img.uiProfilePhoto.profilePic.uiProfilePhotoLarge.img {
	height: 40px;
	width: 40px;
	border: 2px double #fffeff;
}

.profile .profile_color_bar  { background-color:#9fafc5 !important; }

/* navigation */

#pageLogo a    { background:url(http://www.meetingstreetmarket.com/1/FacebookLeather1.png) no-repeat scroll -21px -3px !important;
	margin-left: 5px;
}

#headNavOut    { background:#8b8a8b !important; !important; }
#content       { background-image: url(http://www.meetingstreetmarket.com/1/Leather.png);
	background-repeat: repeat !important;
	font: 11px "Marker Felt";
}
#headNav       { background-image: url(http://www.meetingstreetmarket.com/1/SubNav.png);
	background-repeat: repeat-x;
	height: 37px;
	width: 797px;
	margin-left: 185px;
}

#footerContainer {background-image: url(http://www.meetingstreetmarket.com/1/SubNav.png);
	background-repeat: repeat-x;
	
}

#contentCurve {background-image: url(http://www.meetingstreetmarket.com/1/SubNav.png);
	background-repeat: repeat-x;
	
}
div#headNav.clearfix {
	border: 1px solid #181818;
}

#leftCol {
	background-image: url(http://www.meetingstreetmarket.com/1/Column.png);
	width: 176px;
	padding-left: 4px;
	margin-top: 15px;
}

* { border-color: #2a2c2e !important;}

/* Messages adjustment */
div.UIWell {
	margin-right: 22px;}

div.groupProfileHeaderWash {
	width: 730px;
	margin-left: 2px;}

div.UIImageBlock.clearfix {
	padding-left: 5px;}

a.uiButton {
	margin-right: 30px;}

div#gb_content_and_toolbar {
	width: 735px;}
/* End Message Adjustment */

/* Control Remarks Color */
#home_stream, .ufiItem, .mall_divider_text {background: rgba(240,230,170,.01) !important;
}

div.home_right_column {
	margin-right: 15px;
	margin-left: 8px;
}

div.ego_section {
	margin-right: 15px;
}

#event_create_mini_form div.mbs input.inputtext.event_name.DOMControl_placeholder {
	width: 215px;
}

div#pageHead.ptm.clearfix {
	width: 970px;background-image: url(http://www.meetingstreetmarket.com/1/Leather.png);
	background-repeat: repeat-x;
}

#headNav {width: 765px !important;
		  border-right: none !important;}

div#contentCol.hasRightCol {	
    border-left-style: solid;
	border-left-color: #222;}

/* main nav icons */ 
#jewelRequest { 
   background-image: url("http://www.meetingstreetmarket.com/1/FacebookLeather1.png") !important;
   background-position: -104px -107.5px !important;
}
#jewelMail { 
  background-image: url("http://www.meetingstreetmarket.com/1/FacebookLeather1.png") !important;
  background-position: -104px -75px !important;
}
#jewelNotif { 
  background-image: url("http://www.meetingstreetmarket.com/1/FacebookLeather1.png") !important;
  background-position: -104px -139px !important;
}
#jewelCase .jewel  { border:none !important; }
.jewelToggler:hover { background-color: #888689; }


/* search */
#navSearch       { border-color: !important; }
#navSearch input { border-color: !important; }
#navAccountLink:hover { color: #aeb0b0; }


/* nav tabs */
#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#343434 !important; color: #ecebeb; }


/* boxes */
.lightblue_box  { background-color: #8b8c91; border-color !important; }
.commentable_item .ufi_section  { background-color: #8f8a8d; }
textarea, .inputtext, .inputpassword  { border-color:#abaaab!important; }


/* links */
a  
    { color: #863432;}

.uiLinkButton input  
    { color: #989a9a;}
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link
    { color: #bec0c0;}
button.as_link  
    { color: #b5b1b5;}


/* side nav */
.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#797979 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color: #9c989c; }
.friends_online_sidebar li.buddy_row a:hover  { background-color: #a09ca1; }

.unread  { background-color:#cfcdce !important; }
.line  { color: #878487;
	background-color: #f6eccb;
}


/* pop up box  */
#jewelCase .jewelFooter a   { background-color:#6a6b6e !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color: #fbfbfb;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color: #fffeff; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#cfcece !important; border-color:#aeadad !important; }
#jewelCase .jewelBox  { background-color:#ecebeb !important; }



/* copy */
.uiHeader h2  { color: #000;
	font-family: "Marker Felt";
	margin-top: -4px;
}


/* graffiti */
.current_stuff td  { background-color:#e8e6e7 !important; border-color:#c2c2c2 !important; }


/* profile page */
.UIComposer .UIComposer_Box  { background-color:#e4e2e3 !important; border-color:#cecece !important; }
.profile .box .box_header  { background-color:#e4e2e3 !important; border-color:#cecece !important; }


/* profile tabs */
.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ECECEC !important; border-color:#8f8f8f !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#b1b0b0 !important; border-color:#8f8f8f !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#403f3f !important; color:#ecebeb !important; border-color:#434243 !important; }


/* chat */
#chat_tab_bar .disabled .chat_window .chat_conv  { background-color: #7a7778;  }
#chat_tab_bar .chat_header  { background-color:#8d8d8d !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ecebeb !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color: #fbfbfb; color:#383838!important; }
#presence_bar .presence_menu_opts h2 span  { background:#a4a3a3 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#a8a8a8 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background: #e8e8e8; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#979696 !important; border-color:#777777 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ecebeb !important; }

#presence .presence_menu_opts  { border-color:#9d9c9c !important;
	background-color: #e7e7e7;
}



/* buttons */
.UIButton_Blue  { background:#484848 !important; border-color:#9c9b9b !important; }
.UIComposer_InputArea  { border-color:#b3b2b2 !important; }
.uiButtonConfirm  { background:#515050 !important; border-color:#8e8e8e !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ecebeb !important; }


}
