css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://pnish.livejournal.com/")

{

* {font-family: Cambria, Georgia, serif !important; font-size: 12px;}

body {background-image: url("http://www.strawberrywine.org/personal_images/lj_layouts/pnish_comm/darkplaidbg.png")!important;
font-family: Cambria, Georgia, serif !important;}

.pageblock {
    background-image: url("http://www.strawberrywine.org/personal_images/lj_layouts/pnish_comm/pni_header2-text.png")!important;
background-position: center top!important;
margin-top: 5px!important;
font-family: Cambria,Georgia,serif !important;}

.pageheaderblock {
    height: 400px!important;
font-family: Cambria,Georgia,serif !important;}

.header-menu {padding-top: 380px !important;
line-height: 0!important;
margin-right: 7%!important;
float: right;
text-align: center!important;}

.header-menu li a{
    font-family: Cambria,Georgia,serif !important;}

.header-menu a, .header-menu a:visited {
-moz-border-radius-topleft: 20px!important;
    -moz-border-radius-topright: 20px!important;
padding: 13px!important;}

.sidebar {background-color: #919FA3!important;
-moz-border-radius-bottomright: 20px;
padding-bottom: 30px!important;
width: 250px!important;
margin-right: 10px!important;}

.sidebox {padding-right: 10px;
padding-bottom: 10px;
font-family: Cambria,Georgia,serif !important;}

.sidebox #summary, .sidebox #profile, .sidebox #search {display: none!important;}
.sideboxTitle {display: none!important;}

.bodyblock {
    -moz-border-radius-topleft: 20px!important;
-moz-border-radius-topright: 20px!important;
font-family: Cambria,Georgia,serif !important;
width: 86%!important;}

.entryUserinfo-username, .entryUserinfo-usericon {
    -moz-box-shadow: 2px 2px 3px #706F77!important;
    background-color: #F7F7F7!important;
    border: 1px solid #D5D4D7!important;
    float: right!important;
    padding: 10px!important;
    text-align: left!important;
position: relative !important;
margin-bottom: 8px;
}

.entryUserinfo-usericon img {border: none!important;}

.ljuser img {
    background-image:url("http://www.strawberrywine.org/personal_images/lj_layouts/pnish_comm/diamonds_4.png")!important;
    background-repeat:no-repeat!important;
    height:0!important;
    margin-left:-2px!important;
    padding:0 11px 16px 6px !important;
    width:0!important;
-moz-border-radius:0 !important;
-webkit-border-radius:0 !important;
    }

.ljuser img[src="http://l-stat.livejournal.com/img/userinfo.gif?v=1"] { background-image: url(http://www.strawberrywine.org/personal_images/lj_layouts/pnish_comm/diamonds_4.png)!important; background-repeat:no-repeat!important; padding: 0 11px 16px 6px !important; margin-left:-2px!important; }

.ljuser img[src="http://l-stat.livejournal.com/img/community.gif?v=1"] { background-image: url(http://www.strawberrywine.org/personal_images/lj_layouts/pnish_comm/umbrella.png)!important; background-repeat:no-repeat; padding: 0 11px 16px 6px !important; margin-left:-2px!important; }

.sidebox #tags_sidebox {
    margin-left: 0;
    max-height: 300px;
    overflow-y: scroll;
margin-right: 5px!important;
font-family: Cambria,Georgia,serif !important;
}


.sidebox #tags_sidebox a {
color: #F7F7F7!important;
border: none!important;
}

.sidebox #tags_sidebox a:hover {
background-color: #F7F7F7!important;
color: #00332D!important;
}

.entryHeader a{
    font-family: Cambria, Georgia, serif !important;
    font-size: xx-large!important;
    font-weight: bold!important;
    letter-spacing: -3px!important;}

.entryHeaderDate {
    margin-bottom: 5px!important;}

.entryText {padding-left: 10px!important;
font-family: Cambria, Georgia, serif!important;
font-size: 120%!important;}

.sidebox #latestmonth table a {
    font-size: 150%;
font-family: Cambria,Georgia,serif !important;}

.latestmonth-active a, .latestmonth-active a:hover {
    color: #FFFFFF;
    font-size: 11px !important;}

.entryMetadata {font-size: 85%!important;}

#footer-menu a, #footer-menu a:visited {
    font-size: 200%!important;}

#footer-menu li {
    margin-right: 20px!important;}

}
