@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("algarrobo1.uninorte.edu.co") {

.mybody2, .subtabrow, .menubg {
background: -moz-linear-gradient(left, #111, #444) !important;
}

.backgroundColour {
    background: -moz-linear-gradient(left, #111, #444) !important;
}

.textColour {
    color: #fff !important;
}

.flypull {
    background-color: #bbb !important;
}

.flylinkct {
    color: #500 !important;
}

div.mysidecolumn div {
background-color: rgba(255,255,255,0.45) !important;
-moz-border-radius: 10px;
}

div.mysidecolumn div li.listhead {
background-color: rgba(255,255,255,0.25) !important;
border: none !important;
}

div.mysidecolumn div li.listhead h2 {
    color: #800 !important;
}

div.mycentercolumn div {
-moz-border-radius: 10px;
background: #555 !important;
-moz-box-shadow: 2px 2px 15px #111;
}

div.mycentercolumn div li.listhead {
-moz-border-radius: 10px 10px 0 0;
background: -moz-linear-gradient(left, #111, #444) !important;
}

div.mycentercolumn div ul{
-moz-border-radius: 10px;
background-color: rgba(255,255,255,0.85) !important;
}

.flylinkct {
    background-color: rgba(255,255,255,0.25) !important;
}

div.mycentercolumn div a, h2 a {
font-family: Tahoma;
font-size: 14px;
font-weight: bold;
color: #900 !important;
}

ul.courselist li p {
padding-left: 40px;
padding-top: 5px;
}

