css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("niborea.cz") {

#ministat {
  -moz-border-radius: 10px;
  margin-top: 70px;
}

.maillink, .mailactive, .mailnew {
  -moz-border-radius: 5px;
  padding: 5px 10px !important;
  text-align: center;
  width: 111px !important;
}

.maillink:hover, .mailactive:hover, .mailnew:hover {
  border: 1px solid #ffffff !important;
  color: #fff !important;
  background: #87924D !important;
}

div#mail > div > form {
  -moz-border-radius: 5px;
  padding: 10px 10px 15px 10px !important;
  background: #515B1F;
}

div#mail > div > form > select {
  height: 23px;
  line-height: 23px;
  padding: 3px !important;
}
div#mail > div > form > select > option {
  padding: 3px !important;
}

#mlist, #flist {
  -moz-border-radius: 5px;
  border: 0 !important;
  background: #515B1F;
  margin-bottom: 2em;
  padding: 0 !important;
  font-size: 10pt !important;
}
#mlist tr:nth-child(even), #flist tr:nth-child(even) {
  background: #495918;
}
#mlist tr:last-child td, #flist tr:last-child td {
  padding-bottom: 15px !important;
  border-top: 1px dotted #616B2F !important;
}
#mlist td, #flist td {
  border: 0 !important;
  padding: 5px 5px 5px 0 !important;
}
#mlist td:first-child, #flist td:first-child {
  padding-left: 10px !important;
}
#mlist th, #flist th {
  text-align: center !important;
  font-size: 80%;
  padding: 5px 0 !important;
  border-bottom: 1px dotted #616B2F;
}

}
