css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developer.android.com") {

html, body, td, div#classes-nav, div#packages-nav, div#side-nav, div.sidebox-wrapper, div.sidebox {
background-image: none !important;
color:#ddd !important;
background-color:#222 !important;
}

li a.selected {
padding:2px 4px 2px 4px;
border-radius:2px;
background-color:#666 !important;
}

.gsc-tabHeader.gsc-tabhActive, .gsc-tabHeader.gsc-tabhInactive {
color:#a0c52e !important;
background-color:#000 !important;
border-top:2px solid #a0c52e !important;
border-right:1px solid #a0c52e !important;
border-left:1px solid #a0c52e !important;
}

.gs-snippet {
color:#ddd !important;
}

input.gsc-input {
border:1px solid #a0c52e !important;
}

a, h1, h2, h3{
color:#a0c52e !important;
}

th, div#jd-header {
color:#ddd !important;
background-color:#444 !important;
padding:4px !important;
border-radius:2px;
}

.jd-details-title {
color:#fff !important;
background-color:#000 !important;
}

div.jspContainer {
border:0px !important;
}

a[tabindex="-1"] {
background-image: url('data:image/png;base64,') !important;
margin: 0 0 0 -2000px;
}
div#api-nav-header {
border-bottom:1px solid #181818 !important;
}
div.jspVerticalBar {
background:#181818 !important;
}
div#nav-swap {
border-top:1px solid #181818 !important;
}


div#header {
background-color:#000 !important;
border-radius:2px;
background-repeat: no-repeat;
background-position:10px 20px;
padding-right: 4px !important;
padding-left: 4px !important;
}

.gs-visibleUrl, .gsc-cursor-page{
color:#a0a32e !important;
font-style:italic;
}
b {
color:#a0f52e !important;
}
.special, .selected, .jd-selected {
background-color:#666 !important;
}

input {
color:#fff !important;
}
span.new, span.new-child {
color:#ffff00 !important;
}
code {
color:#a0c52e !important;
}


}
