css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

body {
background-color: gray !important;
background-image: url('http://seun.shizuyue.net/img/mine/index.gif') !important;
font-family: Verdana !important;
align-self: center !important;
}

body.Vanilla, body.Dashboard, body.Conversations {
background: whitesmoke !important;
font-family: Verdana !important;
}

header {
background-color: #38ABE3 !important;
}

header#main-header {
border-radius: 0 0 5px 5px !important;
margin-top: -8px !important;
padding-bottom: 10px !important;
}

header h1 {
color: white !important;
font-family: "Helvetica Neue", Helvetica, arial, sans-serif;
font-size: 24px;
text-align: center;
}

header h1 a {
color: #E3F4FF !important;
}

header h1 a:hover {
color: white !important;
}

nav {
margin-top: 20px !important;
margin-bottom: 20px !important;
-moz-border-radius: 3px;
border-radius: 3px; 
padding: 5px !important;
width: 155px !important;
margin-left: 10px !important;
background-color: #333333 !important;
color: white !important;
border: 1px solid #3D3D3D !important;
}

nav li {
margin-left: 5px !important;
margin-right: 5px !important;
margin-bottom: 10px !important;
font-size: 14px !important;
font-weight: bold !important;
}

nav li a {
color: gainsboro !important;
text-decoration: none !important;
line-height: 20px !important;
}

nav li a:hover {
color: white !important;
}

nav #simple-search input#search-terms {
width: 110px !important;
margin-bottom: 20px !important;
}
  
nav #show-advanced-search {
margin-top: -15px !important;
margin-bottom: 20px !important;
}


nav:hover {
border: 1px solid dimgray !important;
}

#left-sidebar .ad {
display: none !important;
}

#content-wrapper {
margin-top: 20px !important;
}

#content-wrapper article {
padding: 10px 10px 20px 10px !important;
background-color: whitesmoke !important;
-moz-border-radius: 5px;
border-radius: 5px; 
-webkit-box-shadow: 2px 2px 5px #5e5e5e;
-moz-box-shadow: 2px 2px 5px #5e5e5e;
box-shadow: 2px 2px 5px #5e5e5e; 
min-height: 700px !important;
font-family: Noto Sans !important;
}

#content-wrapper article section {
margin-bottom: 10px !important;
}
  
h2 {
font-weight: normal !important;
font-style: normal !important;
}

#front-page-best, #front-page-newest {
width: 50% !important;
}

#long-description {
margin-bottom: 20px !important;
line-height: 1.5em !important;
}
  
  #screenshots {
    background-color: white !important;
  }

#install {
-moz-border-radius: 5px;
border-radius: 5px; 
border: 1px solid #D6D6D6 !important;
background-color: #ACE1AF !important;
width: 90% !important;
}

#related {
-moz-border-radius: 5px;
border-radius: 5px; 
padding: 5px !important;
width: 155px !important;
margin-left: 10px !important;
background-color: #333333 !important;
color: white !important;
border: 1px solid #3D3D3D !important;
}

#related:hover {
border: 1px solid dimgray !important;
}

#related ul {
margin-top: 10px !important;
}

#related li {
margin-bottom: 15px !important;
}

#related li a {
color: gainsboro !important;
text-decoration: none !important;
}

#related li a:hover {
color: white !important;
}

#screenshots {
position: relative !important;
margin-right: 5px !important;
}

#screenshots img {
max-width: 300px !important;
max-height: 200px !important;
}

#content-wrapper li {
margin-bottom: 2px !important;
}

#content-wrapper #style-table {
width: 96% !important;
}

#content-wrapper article .pagination {
padding-top: 10px !important;
font-size: 14px !important;
}

footer p {
padding-top: 20px !important;
margin-left: 15% !important;
}
  
  #obsoletion-message {
    background-color: #F6CED8 !important;
  }

}
