css:
@-moz-document url("chrome://brief/content/brief.xul")
{

#searchbar,
#feed-title,
.view-list-item,
treechildren::-moz-tree-cell-text()
{
	font-family: Tahoma !important;
}

#brief-window
{
	background: black !important;

}

#update-progress,
#searchbar
{
	border-radius: 1px !important;
	background: -moz-radial-gradient(top 0deg, ellipse farthest-side, rgb(51, 51, 51), rgb(0, 0, 0)) !important;
}

#sidebar-separator,
#feed-view-header
{
	border-bottom: 1px solid !important;
}

#sidebar-separator
{
	margin: 0 6px 0 12px !important;
}

#feed-view-header
{
	padding: 0 !important;
	margin: 0 24px 8px 12px !important;
}

#sidebar-splitter
{
	margin: 12px 0 12px 0 !important;
	width: 2px !important;
}

#reveal-sidebar-button
{
	margin: 12px 0 12px 12px !important;
}

#reveal-sidebar-button,
#sidebar-splitter
{
	border: 0 !important;
	background: white !important;
}

#headlines-checkbox,
#filter-unread-checkbox
{
	border: 1px solid transparent !important;
}

#headlines-checkbox[checked],
#filter-unread-checkbox[checked]
{
	border-color: rgb(122, 134, 146) !important;
}

#sidebar-separator,
#sidebar-splitter,
#reveal-sidebar-button,
#feed-view-header,
#update-progress,
#searchbar
{
	border-color: white !important;
}

#searchbar:hover
{
	border-color: rgb(91, 146, 213) !important;
	box-shadow: 0 0 16px rgb(91, 146, 213) !important;
}

#filter-starred-checkbox,
#starred-folder,
#trash-folder
{
	visibility: collapse !important;
}

#sidebar,
#feed-view-header
{
	background: transparent !important;
}

.view-list-item,
treechildren::-moz-tree-cell-text(),
#feed-title,
#searchbar
{
	color: white !important;
}

.view-list-item,
treechildren::-moz-tree-row()
{
	margin: 0 6px 0 6px !important;
	border-radius: 2.5px !important;
	border: 1px solid black !important;
}

.view-list-item[selected],
treechildren::-moz-tree-row(selected)
{
	border-color: rgb(166, 166, 166) !important;
	background: rgba(255, 255, 255, .1) !important;
}

.view-list-item:hover,
treechildren::-moz-tree-row(hover)
{
	border-color: rgb(91, 146, 213) !important;
	background: -moz-radial-gradient(ellipse farthest-side, transparent, rgba(91, 146, 213, .4)) !important;
}

}

@-moz-document url("resource://brief-content/feedview-template.html")
{

BODY,
.article-container
{
	background: transparent !important;
}

.article-star,
.mark-read,
.delete-entry,
.restore-entry
{
	visibility: collapse !important;
}

#feed-content,
.article-container
{
	margin: 0 !important;
	padding: 0 !important;
	max-width: none !important;
}

#feed-content
{
	margin-left: 12px !important;
	margin-right: 10px !important;
}

.article-container,
.article-header
{
	border: none !important;
}

.article-container
{
	margin: 8px 0 8px 0 !important;
	border-radius: 2.5px !important;

	border: 1px solid rgb(166, 166, 166) !important;
	box-shadow: inset 0 0 16px rgba(166, 166, 166, .4) !important;
	background: rgba(255, 255, 255, .1) !important;
}

.article-container:hover
{
	border-color: rgb(91, 146, 213) !important;
	box-shadow: 0 0 16px rgb(91, 146, 213) !important;
	background: -moz-radial-gradient(top, ellipse farthest-side, rgb(51, 51, 51), rgb(0, 0, 0)) !important;
}

#main-message,
#secondary-message,
.article-container DIV,
.article-container SPAN,
.article-container .feed-name,
.article-container .article-date,
.article-container .article-authors,
.article-container .article-content,
.article-container .article-title-link
{
	cursor: default !important;
	font-family: Tahoma !important;

	background: transparent !important;
	color: white !important;
}

.article-authors:hover,
.article-title-link:hover
{
	cursor: pointer !important;
	color: rgb(91, 146, 213) !important;
}

}
