css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") 
{

html, body[bgcolor] 
	{
		background-image:url(http://www.majhost.com/gallery/YotsubatoDesu/Yotsubato/yotsuba.jpg) !important;
		background-position:center !important;
		background-repeat:no-repeat !important;
		background-attachment:fixed !important;
		color: #000 !important;
	}
.logo 
	{ 
		padding: 8px;
		padding-top: 12px !important;
		margin-top: 3px;		
		margin-bottom: 4px;
		background: #eef2ff;
		width: 302px !important;
		margin-left:auto;
		margin-right:auto;
		-moz-border-radius: 3px !important;
		text-align: center;
	}
body 
	{ 
		font: 12px Calibri !important;
		margin: 0 auto !important;
		padding: 0;
	}
form 
	{ 
		margin-left: 4px;		
		width: 75em !important; 
		padding: 2px 2px 2px 2px; 
		max-width: 800px !important;
		background: #eef2ff;
		-moz-border-radius: 3px !important; 
	}
#header 
	{ 
		width: 100% !important;
	}
#navtop 
	{ 
		font-size: smaller !important; 
		border-bottom: 1px solid #34345c !important;
		top: 0; 
		background: #d6daf0 !important;
		position: fixed !important;  
		width: 100% !important; 
		padding: 6px !important; 
		text-align: center !important;
	}
SPAN[style="left: 5px; position: absolute;"] 
	{ 
		display:none !important;
	}
center font[color="red"]
	{ 
		display: none !important;		
		font-size: 13px !important; 
		background: #eef2ff;
		padding: 8px;
		padding-left: 12px !important;
		padding-right: 12px !important;
		-moz-border-radius: 3px !important;
		margin-bottom: 4px;
	}
BODY TABLE[width="100%"] 
	{
		padding: 0 !important;
		padding-left: 10px !important;
		padding-right: 10px !important;
		margin-bottom: 4px; 
	}
BODY TABLE[width="100%"] TH 
	{ 
		padding: 8px !important;
		-moz-border-radius:8px !important;
	}
.postarea
	{ 
		width: auto !important; 
	}
.postarea form 
	{ 
		padding-top: 15px ! important;
		padding-bottom: 10px !important;
	}
.postarea table 
	{
		margin-top: -10px;
		margin-bottom: -10px;
	}
.postblock 
	{
		border: 1px solid #413a67 !important;
		padding: 0px !important;
	}
.postertrip 
	{
		color: #761176 !important;
	}
th 
	{
		padding: 10px !important;
		font-size: small;
	}
td.reply 
	{ 
		width: 1100px !important; 
		border: 0 !important; 
		-moz-border-radius: 3px !important; 
		clear: both;
	}
td.replyhl 
	{ 
		width: 1100px !important; 
		border: 0 !important; 
		-moz-border-radius: 3px !important; 
	}
.pages 
	{
		height: 0px !important;
		font-size: smaller !important; 
		left: 0px; 
		padding: 0px;
		-moz-border-radius: 3px !important; 
		border: 0; 
		border-top: 1px solid #34345c !important;
		width: 100% !important; 
		text-align: center !important; 
		position: fixed; 
		bottom: -1px;
	}
.pages > tbody > tr > td 
	{
		font-size: smaller !important; padding: 0px !important;
	}
.pages > tbody > tr > td > input 
	{
		font: 10px Calibri !important;
	}
/* html > body > div:nth-of-type(5) > form > table > tbody > tr > td input 
	{
		width: auto !important;
	} */
a img 
	{
		clear: both !important; 
		margin: 2px 6px 6px 6px !important; 
	}
.deletebuttons input:not([type="checkbox"]) 
	{ 
		width: 130px !important;
		display: inline !important;
	}
.deletebuttons input[type="checkbox"]
	{
		position: relative;
		top: 3px;
	}
.omittedposts 
	{
		visibility: visible !important;
		margin-left: 12px;
	}
.filesize 
	{ 
		padding-left: 9px; 
		display: inline-block; 
		padding-top: 2px; 
		padding-bottom: 3px;
	}
body form blockquote 
	{ 
		background: #e6eafa; 
		padding: 6px !important; 
		-moz-border-radius: 3px !important; 
		margin: 2px !important; 
		margin-left: 22px !important;
	}
td blockquote 
	{ 
		background: none !important; 
		margin: 0 !important; 
		padding: 6px !important;
	}
.deletebuttons, .futaba 
	{ 
		display: none !important;
	}
img#recaptcha_reload,
img#recaptcha_switch_audio,
img#recaptcha_switch_img,
img#recaptcha_whatsthis 
	{
		display: none !important;
	}
#navtopr,
td small,
hr,
#footer,
.rules 
	{
		display: none !important;
	}

}
