css:
@-moz-document domain("carnage.bungie.org") {

  body > table,
  .main,
  #forumnews,
  #glob_header,
  #misc_msgform,
  #glob_footer,
  .msg_gbnavbar,
  .msg_text br:first-child,
  .glob_navbar br,
  #ind_topinf hr {
    display: none !important;
  }

  * {
    font: 16px/1.6 arial !important;
  }

  table,
  table * {
    border: 0 !important;
    border-collapse: collapse !important;
    border-spacing: 0px !important;
  }
  
  body {
    background: #2b3747 !important;
  }

  #glob_wrapbbs {
    width: 710px !important;
    padding: 20px 35px !important;
    margin: 0 auto !important;
    background: #fff !important;
    border-radius: 7px;
  }

  .glob_navbar {
    margin: 0 0 20px !important;
    padding: 0 !important;
    border: 0 !important;
    width: auto !important;
  }

  #ind_topinf {
    width: auto !important;
    padding: 15px !important;
    margin: 0 0 20px !important;
    border: 0 !important;
    color: #fff !important;
    background: #37516a !important;
    border-radius: 6px;
  }
  #ind_topinf table {
    width: 100% !important;
  }

  #ind_msglist table td {
    line-height: 1.3 !important;
  }
  #ind_msglist table tr:nth-child(odd) {
    background: #E8F0FF !important;
  }

  #ind_msglist {
    width: auto !important;
    margin: 0 !important;
    border: 0 !important;
  }
  #ind_msglist dl,
  #ind_msglist dd {
    margin: 0 !important;
  }
  #ind_msglist dd {
    padding: 15px 20px !important;
    border-bottom: 1px solid #ccc !important;
    line-height: 1.3 !important;
  }
  #ind_msglist dd a {
    font-size: 19px !important;
  }
  #ind_msglist dd:hover {
    background: #fbfbfb !important;
  }


  #msg_gbook {
    width: auto !important;
    margin: auto !important;
  }
  .msg_gbmsg {
    padding: 0 !important;
    margin: 0 0 25px !important;
    border: 1px solid #ddd !important;
    -webkit-border-radius: 4px !important;
    -moz-border-radius: 4px !important;
    -ms-border-radius: 4px !important;
    -o-border-radius: 4px !important;
    border-radius: 4px !important;
    
    background: #fff !important;
    -webkit-transition: 150ms !important;
    -moz-transition: 150ms !important;
  }
  .msg_gbmsg:hover {
    -webkit-box-shadow: 0 1px 10px rgba(140, 140, 140, 0.5) !important;
    -moz-box-shadow: 0 1px 10px rgba(140, 140, 140, 0.5) !important;
  }
  .msg_headln,
  .msg_userln,
  .msg_text {
    width: auto !important;
    padding: 10px 20px !important;
  }
  .msg_headln {
    padding: 7px 20px !important;
    -webkit-border-top-left-radius: 4px !important;
    -webkit-border-top-right-radius: 4px !important;
    -moz-border-radius: 4px 4px 0 0 !important;
    font-weight: bold !important;
    background: #f0f0f0 !important;
  }
  .msg_userln {
    padding: 3px 20px 6px !important;
    border-bottom: 1px solid #eee !important;
    background: #f9f9f9 !important;
  }
  .msg_userln * {
    font-size: 14px !important;
  }
  .msg_text {
    padding: 10px 20px !important;
    background: transparent !important;
  }
  .msg_link {
    width: auto !important;
    padding: 10px 20px !important;
    -webkit-border-radius: 40px !important;
    -moz-border-radius: 40px !important;
    background: transparent !important;
  }

}
